_a-0000ec2b-4d79-8000-9d88-011c48011c48_.sail_code:
if(
  find("<tbody", ri!fillInText),
  /*    if grids are detected they will be skipped*/
  null,
  if(
    find("<cite", ri!fillInText),
    with(
      /*  since the text is split with cite tags the characters right before the fill in is "> - this
 will give the start index of fill in      */
      local!start: if(
        find(""">", ri!fillInText) + 2 = find("[", ri!fillInText),
        if(
          find("]", ri!fillInText) + 1 = find("</cite>", ri!fillInText),
          find(""">", ri!fillInText) + 2,
          find("]", ri!fillInText) + 1
        ),
        find(""">", ri!fillInText) + 2
      ),
      /*  after the fill in it will be either instructions or the end of cite
so if instruction is found that is [<em it will be the end index else the 
end of cite tag  </cite> will be end index     */
      local!end: with(
        local!citeEnd: find("</cite>", ri!fillInText),
        local!insStart1: find("[", ri!fillInText),
        local!insStart2: find("<em", ri!fillInText),
        local!insStart: if(
          and(
            local!insStart1 < local!citeEnd,
            local!insStart1
          ),
          local!insStart1,
          if(
            and(
              local!insStart2 < local!citeEnd,
              local!insStart1
            ),
            local!insStart2,
            0
          )
        ),
        if(
          local!insStart,
          if(
            and(
              local!insStart < local!citeEnd,
              local!start <= local!insStart
            ),
            local!insStart,
            local!citeEnd
          ),
          local!citeEnd
        )
      ),
      local!newStart: if(
        local!start <> local!end,
        if(
          contains(
            { ".", ",", ":" },
            index(ri!fillInText, local!start, null())
          ),
          local!start + 1,
          local!start
        ),
        local!start
      ),
      local!newEnd: if(
        local!start <> local!end,
        if(
          contains(
            { ".", ",", ":" },
            index(ri!fillInText, local!end - 1, null())
          ),
          local!end - 1,
          local!end
        ),
        local!end
      ),
      local!value: mid(
        ri!fillInText,
        local!newStart,
        local!newEnd - local!newStart
      ),
     a!map(
       start: local!newStart,
       end: local!newEnd,
       value: if(rule!AS_GCM_StyledTextEditorForFillInsEnabled_wrapper(),local!value,striphtml(local!value))
     )
    ),
    null
  )
),

