    shape: "SEMI_ROUNDED",
    padding: "STANDARD",
    marginBelow: "STANDARD",
    marginAbove: "LESS",
    showBorder: true,
    borderColor: rule!AS_GCM_getColorBasedOnColorName(colorName: "GRAY_1"),
    decorativeBarPosition: if(
      and(
        contains(
          ri!satisfiedConditionGroupVersionIds,
          ri!ruleConditionGroupVersionId
