    decorativeBarColor: rule!AS_GCM_getColorBasedOnColorName(colorName: "BLUE_2"),
    accessibilityText: if(
      and(
        contains(
          ri!satisfiedConditionGroupVersionIds,
          ri!ruleConditionGroupVersionId
