                ri!refData[recordType!AS GCM R Data.refDataId]
              ),
              null
            ),
            recordType!AS GCM R Data.refKey,
            null
          )
        ),
        color: rule!AS_GCM_getColorBasedOnColorName(colorName: "GRAY_3")
      ),
      char(32),
      char(32),
      a!match(
        value: ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.refClauseDataId],
        equals: cons!AS_GCM_REF_ID_QUESTIONNAIRE_QUESTION_DATA,
        then: ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.responseTemplate.label],
        equals: cons!AS_GCM_REF_ID_CONTRACT_TYPE_DATA,
        then: joinarray(
          index(
            index(
              ri!contractTypes,
              wherecontains(
                tointeger(
                  #"SYSTEM_SYSRULES_fromJson_v1"(
                    ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
                  )
                ),
                ri!contractTypes[recordType!AS GCM R Contract Type.contractTypeId]
              ),
              null
            ),
            recordType!AS GCM R Contract Type.displayValue,
            null
          ),
          ", "
        ),
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_ESTIMATED_DOLLAR_AMOUNT,
        then: a!localVariables(
          /* 1. Define the formatted currency strings in a list */
          local!formattedValues: {
            /* First Currency */
            if(
              isnull(
                ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
              ),
              null,
              #"urn:appian:function:v1:a:currency"(
                isoCode: "USD",
                value: #"SYSTEM_SYSRULES_fromJson_v1"(
                  ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
                ),
                decimalPlaces: 2,
                showSeparators: true(),
                format: "SYMBOL",
                indicatorAlignment: "START"
              )
            ),
            /* Second Currency */
            if(
              isnull(
                ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.compValue]
              ),
              null,
              #"urn:appian:function:v1:a:currency"(
                isoCode: "USD",
                value: #"SYSTEM_SYSRULES_fromJson_v1"(
                  ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.compValue]
                ),
                decimalPlaces: 2,
                showSeparators: true(),
                format: "SYMBOL",
                indicatorAlignment: "START"
              )
            )
          },
          /* 2. Remove the nulls and join with 'And' */
          joinarray(
            reject(fn!isnull, local!formattedValues),
            concat(
              " ",
              "",
              " "
            )
          )
        ),
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_PSC,
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_NAICS_CODE,
        then: #"SYSTEM_SYSRULES_fromJson_v1"(
          ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
        ),
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACTING_OFFICER,
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACT_SPECIALIST,
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACT_OFFICER_REP,
        then: rule!AS_CO_UT_displayUser(
          user: #"SYSTEM_SYSRULES_fromJson_v1"(
            ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
          )
        ),
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACT_START_DATE,
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACT_END_DATE,
        then: joinarray(
          {
            rule!AS_CO_UT_formatDate(
              date: #"SYSTEM_SYSRULES_fromJson_v1"(
                ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
              )
            ),
            rule!AS_CO_UT_formatDate(
              date: #"SYSTEM_SYSRULES_fromJson_v1"(
                /* Defaulting the value here as this can be null */
                a!defaultValue(
                  value: ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.compValue],
                  default: "[]"
                )
              )
            )
          },
          " " & "" & " "
        ),
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACT_CATEGORY,
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CONTRACT_METHOD,
        equals: cons!AS_GCM_REF_ID_CLAUSE_SET_DATA_CLAUSE_SET_TYPE,
        then: joinarray(
          rule!AS_GAM_translateMultipleBundleKeys(
            bundle: ri!bundle,
            bundleKeys: index(
              index(
                ri!refData,
                wherecontains(
                  #"SYSTEM_SYSRULES_fromJson_v1"(
                    ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
                  ),
                  ri!refData[recordType!AS GCM R Data.refDataId]
                ),
                null
              ),
              recordType!AS GCM R Data.refKey,
              null
            )
          ),
          ", "
        ),
        default: #"SYSTEM_SYSRULES_fromJson_v1"(
          ri!ruleConditionVersion[recordType!AS GCM QNM Rule Condition Version.value]
        )
      )
    },
    marginBelow: "NONE"
  )
)

