_a-0000e471-623a-8000-9bc3-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_typeCompare(
    ri!item,
    ""
  ),
  with(
    local!splitText: cast(
      rule!AS_CO_TYPE_VariantList(),
      split(
        ri!item,
        concat(
          "[%",
          ri!index,
          "]"
        )
      )
    ),
    if(
      count(
        local!splitText
      ) < 2,
      local!splitText,
      insert(
        local!splitText,
        ri!richTextArguments[ri!index],
        enumerate(
          length(
            local!splitText
          ) - 1
        ) + 2
      )
    )
  ),
  ri!item
)

