_a-0000eb32-2fbb-8000-9d0d-011c48011c48_.sail_code:
if(
  a!isNullOrEmpty(ri!record),
  ri!record,
  a!localVariables(
    local!allUpdates: rule!AS_CO_UT_rejectNullValuesFromArray(
      a!flatten(
        {
          ri!alwaysOverwrite,
          ri!childRelationships,
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!neverOverwrite,
            expression: #"urn:appian:function:v1:a:update"(
              fv!item,
              "value",
              /*Specifically using this instead of a!defaultValue() to account for the `{null}` value*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: ri!record[fv!item.field],
                replacementValue: fv!item.value
              )
            )
          )
        }
      )
    ),
    local!onlyChangedUpdates: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!allUpdates,
        expression: if(
          or(
            tostring(ri!record[fv!item.field]) = tostring(fv!item.value),
            toboolean(fv!item.applyWhen) = false
          ),
          {},
          fv!item
        )
      )
    ),
    if(
      a!isNullOrEmpty(local!onlyChangedUpdates),
      ri!record,
      #"urn:appian:function:v1:a:update"(
        data: ri!record,
        index: local!onlyChangedUpdates.field,
        value: local!onlyChangedUpdates.value
      )
    )
  )
)

_a-0000e67d-efac-8000-9bcd-011c48011c48_.sail_code:
/*Note: This rule cannot have assertions as the output involves more data (could not add the expected data as assertion value*/
rule!AS_CO_UT_captureMetadata(
  cdt: rule!AS_CO_UT_rejectNullValuesFromArray(
    array: {
      /*Updating the refUsage as null for excluded clauses that got re-added / updated*/
      rule!AS_CO_UT_updateCdtFields(
        cdt: ri!excludedClauses,
        fields: "refUsage",
        newValues: null
      ),
      /*  Deactivating the deleted clauses    */
      rule!AS_CO_UT_updateCdtFields(
        cdt: if(
          rule!AS_CO_UT_isBlank(ri!reviewClauses),
          ri!originalClauseSetClauses,
          rule!AS_CO_UT_Filter(
            records: ri!originalClauseSetClauses,
            field: "relationId",
            value: ri!reviewClauses.relationId,
            filterType: "REJECT"
          )
        ),
        fields: { "isActive" },
        newValues: { false() }
      ),
      if(
        rule!AS_CO_UT_isBlank(ri!reviewClauses),
        null,
        a!localVariables(
          local!refClauses: rule!AS_GCM_QR_getRefClause(
            returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
            clauseIds: rule!AS_CO_UT_indexPath(
              cdt: ri!reviewClauses,
              field: "refClause.clauseId"
            ),
            executeWhen: rule!AS_CO_UT_isNotBlank(
              rule!AS_CO_UT_indexPath(
                cdt: ri!reviewClauses,
                field: "refClause.clauseId"
              )
            ),
            fields: {
              recordType!AS GCM R Clause.clauseId,
              recordType!AS GCM R Clause.clauseTitle,
              recordType!AS GCM R Clause.alternateValue,
              recordType!AS GCM R Clause.effectiveDate,
              recordType!AS GCM R Clause.ucfSectionId,
              recordType!AS GCM R Clause.ucfSection.sectionLabel,
              recordType!AS GCM R Clause.fillIn,
              recordType!AS GCM R Clause.editable
            }
          ),
          local!isUCF: rule!AS_CO_UT_compare(
            value1: ri!clauseSet[recordType!AS GCM Clause Set.refContractFormat.refDataId],
            value2: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_UCF
          ),
          local!sectionMappings: rule!AS_GCM_QR_getRefClauseSectionMapping(
            returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
            executeWhen: not(local!isUCF),
            clauseIds: rule!AS_CO_UT_indexPath(
              cdt: ri!reviewClauses,
              field: "refClause.clauseId"
            ),
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AS GCM R Clause Section Mapping.refSection.contractFormatId,
                operator: "=",
                value: ri!clauseSet[recordType!AS GCM Clause Set.refContractFormat.refDataId]
              )
            },
            fields: {
              recordType!AS GCM R Clause Section Mapping.clauseId,
              recordType!AS GCM R Clause Section Mapping.sectionId,
              recordType!AS GCM R Clause Section Mapping.refSection
            }
          ),
          if(
            /*  Constructing the clause set clauses while creating a clause set        */
            ri!isCreateClauseSet,
            #"SYSTEM_SYSRULES_forEach"(
              items: ri!reviewClauses,
              expression: a!localVariables(
                /*Filtering the reference clause for the current clause id individually*/
                local!refClause: rule!AS_GAM_filterCdtOrRecordDataByField(
                  cdtOrRecord: local!refClauses,
                  field: recordType!AS GCM R Clause.clauseId,
                  value: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "refClause.clauseId")
                ),
                local!section: rule!AS_GCM_filterRecords(
                  records: local!sectionMappings,
                  field: recordType!AS GCM R Clause Section Mapping.clauseId,
                  value: local!refClause[recordType!AS GCM R Clause.clauseId]
                ),
                local!sectionId: if(
                  local!isUCF = true,
                  local!refClause[recordType!AS GCM R Clause.ucfSectionId],
                  local!section[recordType!AS GCM R Clause Section Mapping.sectionId]
                ),
                local!sectionLabel: if(
                  local!isUCF = true,
                  local!refClause[recordType!AS GCM R Clause.ucfSection.sectionLabel],
                  local!section[recordType!AS GCM R Clause Section Mapping.refSection.sectionLabel]
                ),
                rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
                  cdt: fv!item,
                  fieldValuePairs: {
                    {
                      field: "refClause.clauseId",
                      value: local!refClause[recordType!AS GCM R Clause.clauseId]
                    },
                    {
                      field: "clauseTitle",
                      value: a!defaultValue(
                        value: fv!item.clauseTitle,
                        default: local!refClause[recordType!AS GCM R Clause.clauseTitle]
                      )
                    },
                    {
                      field: "alternateValue",
                      value: a!defaultValue(
                        value: fv!item.alternateValue,
                        default: local!refClause[recordType!AS GCM R Clause.alternateValue]
                      )
                    },
                    {
                      field: "section",
                      value: rule!AS_GCM_UT_nullifyEmptyNestedCdtFields(
                        cdt: rule!AS_CO_UT_captureMetadata(
                          cdt: rule!AS_CO_UT_cast(
                            type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_ClauseSection',
                            value: {
                              refSection: recordType!AS GCM R Section(
                                recordType!AS GCM R Section.sectionId: local!sectionId
                              ),
                              sectionLabel: local!sectionLabel,
                              isActive: true
                            }
                          ),
                          modifiedBy: ri!initiator
                        )
                      )
                    },
                    {
                      field: "refStatus.refDataId",
                      value: a!defaultValue(
                        value: fv!item.refStatus.refDataId,
                        default: rule!AS_GCM_BL_returnClauseSetClauseStatusId(
                          isFillin: toboolean(
                            local!refClause[recordType!AS GCM R Clause.fillIn]
                          ),
                          isEditable: toboolean(
                            local!refClause[recordType!AS GCM R Clause.editable]
                          ),
                          formatId: a!defaultValue(
                            value: fv!item.format.refDataId,
                            default: tointeger(
                              local!refClause[recordType!AS GCM R Clause.refFormatId]
                            )
                          )
                        )
                      )
                    },
                    {
                      field: "effectiveDate",
                      value: a!defaultValue(
                        value: fv!item.effectiveDate,
                        default: local!refClause[recordType!AS GCM R Clause.effectiveDate]
                      )
                    },
                    /*Nullifying the excluded clauses usage*/
                    {
                      field: "refUsage",
                      value: if(
                        tointeger(
                          rule!AS_CO_UT_indexPath(cdt: fv!item, field: "refUsage.refDataId")
                        ) = cons!AS_GCM_REF_ID_USAGE_EXCLUDED,
                        null,
                        fv!item.refUsage
                      )
                    },
                    /*Setting isEditedViaEditClause as false()*/
                    {
                      field: "isEditedViaEditClause",
                      value: false()
                    }
                  }
                )
              )
            ),
            /*  Constructing the clause set clauses while editing a clause set        */
            #"SYSTEM_SYSRULES_forEach"(
              items: ri!reviewClauses,
              expression: a!localVariables(
                /*Querying the reference clause for the current clause id individually*/
                local!refClause: rule!AS_GAM_filterCdtOrRecordDataByField(
                  cdtOrRecord: local!refClauses,
                  field: recordType!AS GCM R Clause.clauseId,
                  value: rule!AS_CO_UT_indexPath(cdt: fv!item, field: "refClause.clauseId")
                ),
                local!section: rule!AS_GCM_filterRecords(
                  records: local!sectionMappings,
                  field: recordType!AS GCM R Clause Section Mapping.clauseId,
                  value: local!refClause[recordType!AS GCM R Clause.clauseId]
                ),
                local!sectionId: if(
                  local!isUCF = true,
                  local!refClause[recordType!AS GCM R Clause.ucfSectionId],
                  local!section[recordType!AS GCM R Clause Section Mapping.sectionId]
                ),
                local!sectionLabel: if(
                  local!isUCF = true,
                  local!refClause[recordType!AS GCM R Clause.ucfSection.sectionLabel],
                  local!section[recordType!AS GCM R Clause Section Mapping.refSection.sectionLabel]
                ),
                rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
                  cdt: fv!item,
                  fieldValuePairs: {
                    {
                      field: "refClause.clauseId",
                      value: local!refClause[recordType!AS GCM R Clause.clauseId]
                    },
                    {
                      field: "clauseTitle",
                      value: local!refClause[recordType!AS GCM R Clause.clauseTitle]
                    },
                    {
                      field: "alternateValue",
                      value: local!refClause[recordType!AS GCM R Clause.alternateValue]
                    },
                    {
                      field: "section",
                      value: rule!AS_GCM_UT_nullifyEmptyNestedCdtFields(
                        cdt: rule!AS_CO_UT_captureMetadata(
                          cdt: rule!AS_CO_UT_cast(
                            type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_ClauseSection',
                            value: {
                              refSection: recordType!AS GCM R Section(
                                recordType!AS GCM R Section.sectionId: local!sectionId
                              ),
                              sectionLabel: local!sectionLabel,
                              isActive: true
                            }
                          ),
                          modifiedBy: ri!initiator
                        )
                      )
                    },
                    {
                      field: "refStatus.refDataId",
                      value: rule!AS_GCM_BL_returnClauseSetClauseStatusId(
                        isFillin: toboolean(
                          local!refClause[recordType!AS GCM R Clause.fillIn]
                        ),
                        isEditable: toboolean(
                          local!refClause[recordType!AS GCM R Clause.editable]
                        ),
                        formatId: a!defaultValue(
                          value: fv!item.format.refDataId,
                          default: tointeger(
                            local!refClause[recordType!AS GCM R Clause.refFormatId]
                          )
                        )
                      )
                    },
                    {
                      field: "effectiveDate",
                      value: local!refClause[recordType!AS GCM R Clause.effectiveDate]
                    },
                    { field: "isClauseUpdated", value: false() },
                    if(
                      a!isNullOrEmpty(fv!item.relationId),
                      /*Setting isEditedViaEditClause as false()*/
                      {
                        field: "isEditedViaEditClause",
                        value: false()
                      },
                      {}
                    )
                  }
                )
              )
            )
          )
        )
      )
    }
  ),
  modifiedBy: ri!initiator
)

_a-0000e6cd-618a-8000-9bd9-011c48011c48_.sail_code:
rule!AS_CO_UT_queryEntity(
  entity: cons!AS_GCM_ENT_CLAUSE_SET_TEMPLATE,
  entityType: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_ClauseSet_Template',
  returnType: ri!returnType,
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregation
        }
      ),
      rule!AS_CO_UT_isNotBlank(ri!setId)
    )
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  sort: ri!sort,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: "setId",
