rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS GCM Clause Set Clause Inclusion Reason,
  returnType: a!defaultValue(
    value: ri!returnType,
    default: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregationFields
        }
      ),
      rule!AS_CO_UT_isNotBlank(ri!reasonIds),
      rule!AS_CO_UT_isNotBlank(ri!relationIds)
    )
  ),
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS GCM Clause Set Clause Inclusion Reason.refContractType,
          recordType!AS GCM Clause Set Clause Inclusion Reason.refTemplate,
          recordType!AS GCM Clause Set Clause Inclusion Reason.ruleConditionVersion
        },
        {}
      )
    }
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM Clause Set Clause Inclusion Reason.reasonId,
      operator: "in",
      value: ri!reasonIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM Clause Set Clause Inclusion Reason.relationId,
      operator: "in",
      value: ri!relationIds
    ),
    ri!additionalFilters
  },
  aggregationFields: ri!aggregationFields,
  relatedRecordData: ri!relatedRecordData
)

