  /*Getting the current security configuration of the folder*/
  local!currentSecurity: returncontentrolemap(tofolder(ri!folderId), true),
  if(
    /*Returns true if the folder security is improper or needs update, else returns false*/
    or(
      rule!AS_CO_UT_not(
        contains(
          togroup(local!currentSecurity.editorGroups),
          togroup(rule!AS_GCM_clauseSetDocumentFolderSecurity())
        )
      ),
      rule!AS_CO_UT_not(
        contains(
          togroup(local!currentSecurity.adminGroups),
          togroup(cons!AS_GAM_GRP_APPIAN_ADMINISTRATORS)
        )
      )
    ),
    true,
    false
  )
)

