_a-0000ec46-fcf4-8000-9d91-011c48011c48_.sail_code:
/*This rule is used perform below manipulations to the clause text during sync process

#1 Adding span hidden tags for H1 tags and 'As prescribed' paragraph as we don't 
need to show this in document generation since we are already adding a clause header.

#2 Rectifying the misplaced checkbox cite tags where the checkbox is not enclosed within the cite tag */
if(
  a!isNullOrEmpty(ri!clauseText),
  "",
  a!localVariables(
    local!htmlToHide: regexfirstmatch(
      "<h1.*?As prescribed.*?\d{4}\).*?</p>",
      ri!clauseText
    ),
    /* Adding the hidden tag only for clause text */
    local!clauseText: if(
      and(
        ri!isClauseText,
        /* As some of the DFARS clause text are unstructured */
        a!isNotNullOrEmpty(local!htmlToHide)
      ),
      substitute(
        ri!clauseText,
        local!htmlToHide,
        "<span hidden="""">" & local!htmlToHide & "</span>"
      ),
      ri!clauseText
    ),
    /*Scenario: cite tag for the checkbox is misplaced. It is tagged to the text instead of checkbox. Ex: 52.212-3*/
    /*Retrieving all mistagged checkboxes*/
    local!mistaggedCheckboxes: rule!AS_GCM_UT_regexsearch(
      /* Test this Regex in https://regex101.com/r/LE0egm/1 */
      pattern: "(?:<span>☐<\/span>|☐)(.*?)(<cite.*?" & cons!AS_GCM_TXT_FILL_IN_TYPE_CHECKBOX & """>)([^☐□_]+)<\/cite>",
      searchString: local!clauseText,
      regexFlag: "gm",
      returnGroups: true
    ),
    /*Correcting the mistagged checkboxes*/
    local!retaggedCheckboxes: #"SYSTEM_SYSRULES_forEach"(
      items: local!mistaggedCheckboxes,
      expression: fv!item.groups[2].match & char(9633) & "</cite>" & fv!item.groups[1].match & fv!item.groups[3].match,

    ),
    if(
      rule!AS_CO_UT_isBlank(local!mistaggedCheckboxes),
      local!clauseText,
      rule!AS_GCM_substituteListFromTextUsingReduce(
        text: local!clauseText,
        find: local!mistaggedCheckboxes.match,
        replaceWith: local!retaggedCheckboxes
      )
    )
  )
)

_a-0000e3e6-fb42-8000-9ba5-011c48011c48_.sail_code:
if(
  and(
    rule!AS_CO_UT_compareStrong(
      value1: ri!originalObject,
      value2: ri!newObject
    ),
    ri!externalAction <> cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_INITIATED
  ),
  {},
  a!localVariables(
    local!auditConfig: rule!AS_CO_UT_dynamicDictionary(
      fieldValuePairs: {
        {
          field: ri!auditConfig.idField,
          value: rule!AS_CO_UT_replaceNull(
            nullableValue: index(
              ri!originalObject,
              ri!auditConfig.idField,
              null
            ),
            replacementValue: index(
              ri!newObject,
              ri!auditConfig.idField,
              null
            )
          )
        },
        if(
          rule!AS_CO_UT_isNotBlank(ri!auditConfig.foreignIdField),
          {
            field: if(
              length(
                split(ri!auditConfig.foreignIdField, ".")
              ) = 2,
              split(ri!auditConfig.foreignIdField, ".")[2],
              split(ri!auditConfig.foreignIdField, ".")[1]
            ),
            value: rule!AS_CO_UT_replaceNull(
              nullableValue: rule!AS_CO_UT_indexPath(
                cdt: ri!originalObject,
                field: ri!auditConfig.foreignIdField
              ),
              replacementValue: rule!AS_CO_UT_indexPath(
                cdt: ri!newObject,
                field: ri!auditConfig.foreignIdField
              )
            )
          },
          {}
        ),
        { field: "username", value: ri!username },
        { field: "timestamp", value: now() },
        { field: "auditType", value: ri!auditType },
        {
          field: "auditActionCode",
          value: rule!AS_ADT_UT_determineAuditActionCode(
            originalObject: ri!originalObject,
            newObject: ri!newObject,
            auditConfig: ri!auditConfig,
            externalAction: ri!externalAction
          )
        },
        {
          field: "simpleFieldChanges",
          value: rule!AS_ADT_UT_buildSimpleFieldChanges(
            simpleFields: ri!auditConfig.simpleFields,
            originalObject: ri!originalObject,
            newObject: ri!newObject
          )
        },
        if(
          rule!AS_CO_UT_isBlank(ri!auditConfig.complexFields),
          {},
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!auditConfig.complexFields,
            expression: {
              field: fv!item.auditFieldName,
              value: rule!AS_ADT_UT_calculateAudit_MultipleObjects(
                originalObjects: index(ri!originalObject, fv!item.fieldName, {}),
                newObjects: index(ri!newObject, fv!item.fieldName, {}),
                auditConfig: fv!item,
                username: ri!username,
                externalAction: ri!externalAction
              )
            }
          )
        )
      }
    ),
    if(
      /*To make the action code "UNCHANGED" if the object is unmodified(excluding the metadata)*/
      and(
        rule!AS_CO_UT_not(
          contains(
            a!flatten(
              #"SYSTEM_SYSRULES_forEach"(
                items: {
                  "simpleFieldChanges",
                  index(
                    ri!auditConfig.complexFields,
                    "auditFieldName",
                    null
                  )
                },
                expression: or(
                  rule!AS_CO_UT_isBlank(index(local!auditConfig, fv!item, null)),
                  if(
                    contains(
                      touniformstring(
                        index(
                          ri!auditConfig.complexFields,
                          "auditFieldName",
                          null
                        )
                      ),
                      tostring(fv!item)
                    ),
                    not(
                      contains(
                        #"SYSTEM_SYSRULES_forEach"(
                          items: rule!AS_CO_UT_indexPath(cdt: local!auditConfig, field: fv!item),
                          expression: tostring(fv!item.auditActionCode) = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
                        ),
                        false
                      )
                    ),
                    false
                  )
                )
              )
            ),
            false
          )
        ),
        or(
          tostring(
            index(
              local!auditConfig,
              "auditActionCode",
              null
            )
          ) = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
          ri!externalAction = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MANUAL_UPDATE,
          ri!externalAction = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MANUAL_SYNC
        )
      ),
      rule!AS_CO_UT_updateCdtFields(
        cdt: local!auditConfig,
        fields: "auditActionCode",
        newValues: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
      ),
      local!auditConfig
    )
  )
)

