a!localVariables(
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
  local!acquisitionHelperData: if(
    ri!farMatrixSuccess = false,
    recordType!AS GCM P Acquisition Update Helper(
      recordType!AS GCM P Acquisition Update Helper.farMatrixSuccess: false(),
      recordType!AS GCM P Acquisition Update Helper.farMatrixError: ri!farMatrixError
    ),
    rule!AS_GCM_QR_getAquisitionHelperDetails(
      fields: {
        recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated,
        recordType!AS GCM P Acquisition Update Helper.isNewClause,
        recordType!AS GCM P Acquisition Update Helper.syncFailure,
        recordType!AS GCM P Acquisition Update Helper.farMatrixError,
        recordType!AS GCM P Acquisition Update Helper.refClause.clauseNumber,
        recordType!AS GCM P Acquisition Update Helper.noDataReturned,
        recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned,
        recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned,
        recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber,
        recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber,
        recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated,
        recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated
      },
      processId: ri!processId
    )
  ),
  local!updatedClauses: if(
    ri!farMatrixSuccess = false,
    0,
    count(
      rule!AS_CO_UT_distinct(
        value: {
          rule!AS_GCM_filterRecords(
            records: local!acquisitionHelperData,
            field: recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated,
            value: true
          ),
          rule!AS_GCM_filterRecords(
            records: local!acquisitionHelperData,
            field: recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated,
            value: true
          ),
          rule!AS_GCM_filterRecords(
            records: local!acquisitionHelperData,
            field: recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated,
            value: true
          )
        }
      )
    )
  ),
  local!newClauses: if(
    ri!farMatrixSuccess = false,
    0,
    rule!AS_GCM_filterRecords(
      records: local!acquisitionHelperData,
      field: recordType!AS GCM P Acquisition Update Helper.isNewClause,
      value: true,
      filterType: "MATCH_COUNT"
    )
  ),
  local!failedSyncClauses: if(
    ri!farMatrixSuccess = false,
    0,
    rule!AS_GCM_filterRecords(
      records: local!acquisitionHelperData,
      field: recordType!AS GCM P Acquisition Update Helper.syncFailure,
      value: true,
      filterType: "MATCH_COUNT"
    )
  ),
  "<!DOCTYPE html>
<html>
<head>
<title>Page Title</title>
<style>
table {
width:100%;
}
table, th, td {
border: 1px solid black;
border-collapse: collapse;
}
th, td {
padding: 15px;
text-align: left;
}
tr:nth-child(even) {
background-color: #eee;
}
tr:nth-child(odd) {
background-color: #fff;
}
th {
background-color: black;
color: white;
}
</style>
</head>
<body>

<h4> " & if(
  ri!isZipImport <> true(),
  a!match(
    value: ri!sourceId,
    equals: cons!AS_GCM_REF_ID_SOURCE_DFARS,
    then:"The following are the results of the DFARS weekly sync of clauses from acquisition.gov",
    default: "The following are the results of the FAR weekly sync of clauses from acquisition.gov"
  ),
  a!match(
    value: ri!sourceId,
    equals: cons!AS_GCM_REF_ID_SOURCE_DFARS,
    then:"The following are the results of the DFARS clause zip import",
    default: "The following are the results of the FAR clause zip import"
  ),
  ) & "</h4>
<p> " & "" & local!updatedClauses & " </p>
<p>" & "" & local!newClauses & " </p>
<p>" & "" & local!failedSyncClauses & "</p><p><b>" & if(
    and(
      local!failedSyncClauses = 0,
      local!newClauses = 0,
      local!updatedClauses = 0,
      rule!AS_CO_UT_isBlank(
        index(
          local!acquisitionHelperData,
          recordType!AS GCM P Acquisition Update Helper.farMatrixError,
          null()
        )
      )
    ),
    "",
    if(
      and(
        local!failedSyncClauses = 0,
        local!newClauses = 0,
