/*1.We can include all possible combination of months in the expression if we need to*/
/*2.Logic and patterns are different for original and alternate clauses*/
if(
  a!isNullOrEmpty(ri!clauseHtml.result.body),
  null,
  a!localVariables(
    /*patterns to match with effective date in sam.gov*/
    local!pattern: "Ctr_SmCaps.*?\((Jan|Feb|Mar|Apr|May|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec) \d{4}\)|Ctr_SmCaps.*?\((Jan|Feb|Mar|Apr|May|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec).*?\d{4}\)|Ctr_SmCaps.*?\((Jan|Feb|Mar|Apr|May|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec).\d{4}\)|Ctr_SmCaps.*?\((.*?| |Jan|Feb|Mar|Apr|May|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec).*?\d{4}\)",
    /*Doc html from acquistion*/
    local!getDocHtml: ri!clauseHtml.result.body,
    /*final pattern for month and year*/
    local!finalRegexSearch: "([(]?(Jan|Feb|Mar|Apr|May| Jun|Jun|June|Jul|July| Jul|Aug|Sep|Sept|Oct|Nov|Dec)( |.*?)\d{4})",
    /*using regex to get the exact output*/
    local!output: a!flatten(
      a!localVariables(
        local!matches: regexallmatches(local!pattern, local!getDocHtml),
        if(
          rule!AS_CO_UT_isNotBlank(local!matches),
          index(
            rule!AS_GCM_UT_regexsearch(
              pattern: local!finalRegexSearch,
              searchString: striphtml(local!matches),
              regexFlag: "i"
            ),
            "match",
            null
          ),
          {}
        )
      )
    ),
    /*removing html*/
    local!plainText: striphtml(union(local!output, local!output)),
    local!finalOutput: cleanwith(
      local!plainText,
      "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789"
    ),
    local!baseClauseEffectiveDate: if(
      rule!AS_CO_UT_isBlank(local!finalOutput),
      null,
      proper(
        left(local!finalOutput, 3) & " " & right(local!finalOutput, 4)
      )
    ),
    local!alternateClauseEffectiveDate: proper(
      regexfirstmatch(
        "(?<=ALTERNATE " & ri!alternateValue & " \()(.*?)(?=\))",
        striphtml(local!getDocHtml)
      )
    ),
    if(
      and(
        a!isNotNullOrEmpty(ri!alternateValue),
        regexmatch(
          local!finalRegexSearch,
          local!alternateClauseEffectiveDate
        )
      ),
      local!alternateClauseEffectiveDate,
      local!baseClauseEffectiveDate
    )
  )
)

