a!localVariables(
  local!clauseHtml: rule!AS_CO_UT_castToMap(
    rule!AS_GCM_getFileDataViaZipDocumentOrGitHub(
      clauseNumber: ri!clauseNumber,
      zipDocument: ri!zipDocument,
      executeWhen: true(),
      sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
    )
  ),
  if(
    toboolean(local!clauseHtml.success) = false,
    null,
    a!localVariables(
      local!clauseHtmlBody: local!clauseHtml.result.body,
      local!refType: rule!AS_GCM_DFARS_getRefTypeFromSync(clauseText: local!clauseHtmlBody),
      local!alternateValue: rule!AS_GCM_DFARS_getAlternateValuesFromSync(clauseHtmlBody: local!clauseHtmlBody),
      local!prescriptionNumber: rule!AS_GCM_DFARS_getPrescriptionNumberFromSync(clauseHtml: local!clauseHtmlBody),
      local!editable: rule!AS_GCM_DFARS_updateEditableFromSync(clauseHtml: local!clauseHtmlBody),
      local!fillins: rule!AS_GCM_updateFillInFromSync(clauseHtml: local!clauseHtml),
      local!existingClauseDetails: rule!AS_GCM_QR_getRefClause(
        excludeDeviations: true,
        returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
        executeWhen: true,
        /*  Including "" to also filter the base clause  */
        alternateValues: { "", local!alternateValue },
        clauseNumbers: ri!clauseNumber
      ),
      local!prescriptionHtml: rule!AS_GCM_getFileDataViaZipDocumentOrGitHub(
        zipDocument: ri!zipDocument,
        clauseNumber: local!prescriptionNumber,
        executeWhen: a!isNotNullOrEmpty(local!prescriptionNumber),
        sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
      ),
      a!map(
        existingRefClauses:local!existingClauseDetails,
        refClauses: a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: append(local!alternateValue, ""),
            expression: a!localVariables(
              local!existingClause: rule!AS_GCM_filterRecords(
                records: local!existingClauseDetails,
                field: recordType!AS GCM R Clause.clauseNumberWithAlternate,
                value: joinarray({ ri!clauseNumber, fv!item }, " | ")
              ),
              /*Check if the Clause Number is invalid*/
              local!isNewClause: rule!AS_CO_UT_isBlank(local!existingClause),
              local!refClauseDetails: if(
                rule!AS_CO_UT_isBlank(ri!clauseNumber),
                null,
                /*Updating the existing clause in appian / creating a new entry if new*/
                rule!AS_GCM_setRecordFields(
                  record: a!defaultValue(
                    value: local!existingClause,
                    default: recordType!AS GCM R Clause()
                  ),
                  alwaysOverwrite: {
                    a!map(
                      field: recordType!AS GCM R Clause.refTypeId,
                      value: local!refType
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.refSourceId,
                      value: cons!AS_GCM_REF_ID_SOURCE_DFARS
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.clauseNumber,
                      value: ri!clauseNumber
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.clauseTitle,
                      value: ri!clauseTitle
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.alternateValue,
                      value: fv!item
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.editable,
                      value: local!editable
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.effectiveDate,
                      value: rule!AS_GCM_DFARS_updateEffectiveDateFromSync(
                        clauseHtml: local!clauseHtml,
                        alternateValue: fv!item
                      )
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.fillIn,
                      value: local!fillins
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.prescriptionNumber,
                      value: local!prescriptionNumber
                    )
                  }
                )
              ),
              local!acquisitionHelper: rule!AS_GCM_setRecordFields(
                record: recordType!AS GCM P Acquisition Update Helper(),
                alwaysOverwrite: {
                  a!map(
                    field: recordType!AS GCM P Acquisition Update Helper.processId,
                    value: ri!processId
                  ),
                  a!map(
                    field: recordType!AS GCM P Acquisition Update Helper.isNewClause,
                    value: local!isNewClause
                  ),
                  a!map(
                    field: recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated,
                    value: and(
                      rule!AS_CO_UT_isNotBlank(local!refClauseDetails),
                      not(
                        rule!AS_CO_UT_compare(
                          value1: local!refClauseDetails,
                          value2: local!existingClause
                        )
                      )
                    ),
                    applyWhen: local!isNewClause <> true
                  ),
                  {
                    field: recordType!AS GCM P Acquisition Update Helper.farMatrixSuccess,
                    value: ri!dfarsSuccess
                  },
                  {
                    field: recordType!AS GCM P Acquisition Update Helper.farMatrixError,
                    value: ri!dfarsSuccess
                  },
                  {
                    field: recordType!AS GCM P Acquisition Update Helper.noDataReturned,
                    value: rule!AS_CO_UT_isBlank(value: local!refClauseDetails)
                  },
                  {
                    field: recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber,
                    value: false
                  }
                }
              ),
              local!updatedClauseDetails: if(
                rule!AS_CO_UT_isBlank(ri!clauseNumber),
                null,
                /*Updating the existing clause in appian / creating a new entry if new*/
                rule!AS_GCM_setRecordFields(
                  record: local!refClauseDetails,
                  alwaysOverwrite: {
                    a!map(
                      field: recordType!AS GCM R Clause.refTypeId,
                      value: local!refType
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.refSourceId,
                      value: cons!AS_GCM_REF_ID_SOURCE_DFARS
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.clauseNumber,
                      value: ri!clauseNumber
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.clauseTitle,
                      value: ri!clauseTitle
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.alternateValue,
                      value: fv!item
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.editable,
                      value: local!editable
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.effectiveDate,
                      value: rule!AS_GCM_DFARS_updateEffectiveDateFromSync(
                        clauseHtml: local!clauseHtml,
                        alternateValue: fv!item
                      )
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.fillIn,
                      value: local!fillins
                    ),
                    a!map(
                      field: recordType!AS GCM R Clause.prescriptionNumber,
                      value: local!prescriptionNumber
                    )
                  },
                  neverOverwrite: {
                    a!map(
                      field: recordType!AS GCM R Clause.clauseId,
                      value: local!existingClause[recordType!AS GCM R Clause.clauseId]
                    )
                  },
                  childRelationships: a!map(
                    field: recordType!AS GCM R Clause.acquisitionUpdateHelper,
                    value: local!acquisitionHelper
                  )
                )
              ),
              rule!AS_GCM_setMetaDataForClauseRecord(
                clauses: local!updatedClauseDetails,
                initiator: ri!initiator,
                isClauseUpdated: or(
                  index(
                    local!acquisitionHelper,
                    recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated,
                    false
                  ),
                  rule!AS_CO_UT_isNotBlank(
                    index(
                      local!acquisitionHelper,
                      recordType!AS GCM P Acquisition Update Helper.refClause.effectiveDate,
                      null
                    )
                  ),
                  rule!AS_CO_UT_isNotBlank(
                    index(
                      local!acquisitionHelper,
                      recordType!AS GCM P Acquisition Update Helper.refClause.fillIn,
                      null
                    )
                  ),
                  local!isNewClause
                ),
                isSyncProcess: true()
              ),

            )
          )
        ),
        clauseHtml: local!clauseHtml,
        prescriptionHtml: local!prescriptionHtml
      )
    )
  )
)

