  fn!error(concat("`pagingInfo` must be null when the `returnType` is not DATA_SUBSET, instead use `sort` to pass custom-sorting and `startIndex` & `batchSize` will be automatically calculated for you")),
  and(
    not(isnull(ri!pagingInfo)),
    rule!AS_CO_UT_isNotBlank(ri!sort)
