    ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    rule!AS_CO_UT_isNotBlank(ri!fields)
  ),
  fn!error("You cannot provide an input for `fields` when `returnType` is AGGREGATION.  Use `aggregationFields` instead."),
  and(
    ri!returnType <> cons!AS_CO_ENUM_QE_RETURN_TYPE_AGGREGATION,
    rule!AS_CO_UT_isNotBlank(ri!aggregationFields)
