      a!isNullOrEmpty(ri!zipDocument),
      /*Use integration*/
      contains(
        touniformstring(
          rule!AS_GCM_getDitaTree(
            executeWhen: true(),
            sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
          ).path
        ),
