      ),
      and(
        toboolean(local!response.success),
        a!localVariables(
          local!allClauses: rule!AS_GCM_DFARS_extractClausesFromXml(
            dfarsDocOrXml: if(
              a!isNotNullOrEmpty(ri!zipDocument),
              local!response.result.body,
              todocument(local!response.result.body)
            )
          ),
          /*Checking if all the clauses have unique clause numbers*/
          length(
            rule!AS_CO_UT_distinct(
              value: a!flatten(
                #"SYSTEM_SYSRULES_forEach"(
                  items: enumerate(local!clausesToCheck / 2),
                  expression: {
                    local!allClauses[fv!index].clauseNumber,
                    local!allClauses[length(local!allClauses) - fv!item].clauseNumber
                  }
                )
              )
            )
