    executeWhen: true,
    clauseId: ri!refClause[recordType!AS GCM R Clause.clauseId]
  ),
  local!clauseText: if(
    index(ri!clauseHtml, "success", null) = "true",
    rule!AS_GCM_UT_formatMergedCells(
      clauseText: rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
        responseBody: ri!clauseHtml.result.body,
        isClauseText: true()
      )
    ),
    ""
  ),
  /*Check if invalid Prescription number*/
  local!invalidPrescriptionNumber: rule!AS_CO_UT_isBlank(value: ri!prescriptionHtml),
  /*Fetch Prescription Text*/
  local!prescriptionText: if(
    index(ri!prescriptionHtml, "success", null) = "true",
    rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
      responseBody: ri!prescriptionHtml.result.body,
      isClauseText: false()
    ),
    ""
  ),
  local!clauseTextUpdated: and(
    rule!AS_CO_UT_isNotBlank(input: local!clauseText),
    not(
      rule!AS_CO_UT_compare(
        value1: local!existingRefClauseText.clauseText,
        value2: local!clauseText
      )
    )
  ),
  local!prescriptionTextUpdated: and(
    rule!AS_CO_UT_isNotBlank(input: local!prescriptionText),
    not(
      rule!AS_CO_UT_compare(
        value1: local!existingRefClauseText.prescriptionText,
        value2: local!prescriptionText
      )
    )
  ),
  local!acquisitionHelper: rule!AS_GCM_setRecordFields(
    record: ri!refClause[recordType!AS GCM R Clause.acquisitionUpdateHelper],
    alwaysOverwrite: {
      {
        field: recordType!AS GCM P Acquisition Update Helper.clauseTextError,
        value: index(ri!clauseHtml, "error", null)
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.clauseTextSuccess,
        value: index(ri!clauseHtml, "success", null) = "true"
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned,
        value: rule!AS_CO_UT_isBlank(value: local!clauseText)
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated,
        value: if(
          ri!refClause[recordType!AS GCM R Clause.acquisitionUpdateHelper.isNewClause],
          null,
          local!clauseTextUpdated
        )
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber,
        value: local!invalidPrescriptionNumber
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.prescriptionTextError,
        value: index(ri!prescriptionHtml, "error", null)
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess,
        value: index(ri!prescriptionHtml, "success", null)
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned,
        value: rule!AS_CO_UT_isBlank(value: local!prescriptionText)
      },
      {
        field: recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated,
        value: if(
          ri!refClause[recordType!AS GCM R Clause.acquisitionUpdateHelper.isNewClause],
          null,
          local!prescriptionTextUpdated
        )
      }
    }
  ),
  a!map(
    /*Updating the existing clause in appian / creating a new entry if new*/
    refClause: rule!AS_GCM_setMetaDataForClauseRecord(
      clauses: rule!AS_GCM_setRecordFields(
        record: ri!refClause,
        childRelationships: a!map(
          field: recordType!AS GCM R Clause.acquisitionUpdateHelper,
          value: local!acquisitionHelper
        )
      ),
      initiator: ri!initiator,
      isClauseUpdated: or(
        local!clauseTextUpdated,
        local!prescriptionTextUpdated
      ),
      isSyncProcess: true()
    ),
    refClauseText: rule!AS_GCM_setMetaDataForRefClauseTextCdt(
      initiator: ri!initiator,
      refClauses: ri!refClause,
      refClauseTexts: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
        cdt: local!existingRefClauseText,
        fieldValuePairs: {
          {
            field: "clauseText",
            value: local!clauseText
          },
          {
            field: "prescriptionText",
            value: local!prescriptionText
          }
        }
      ),
      isSyncProcess: true
    )
  )
)

