    processData: ri!clauses,
    recordsUpdated: ri!recordsUpdated,
    fksToParent: {
      recordType!AS GCM R Clause.acquisitionUpdateHelper.refClauseId,
      recordType!AS GCM R Clause.additionalSections.clauseId
    }
  )
