_a-0000e4b1-2fbc-8000-9bc6-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS GCM QNM T Question Precedent,
  returnType: a!defaultValue(
    value: ri!returnType,
    default: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS GCM QNM T Question Precedent.questionTemplate,
          recordType!AS GCM QNM T Question Precedent.responseRequirement
        },
        {}
      )
    }
  ),
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM QNM T Question Precedent.questionPrecedentTemplateId,
      operator: "=",
      value: ri!questionPrecedentTemplateId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM QNM T Question Precedent.questionTemplateId_precedent,
      operator: "in",
      value: ri!questionTemplateId,
      applyWhen: rule!AS_CO_UT_isNotBlank(ri!questionTemplateId)
    )
  },
  aggregationFields: ri!aggregationFields,
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: or(
    ri!executeWhen,
    any(
      rule!AS_CO_UT_isNotBlank(_),
      {
        ri!additionalFilters,
        ri!logicalExpression,
        ri!aggregationFields,
        ri!questionPrecedentTemplateId,
        ri!questionTemplateId
      }
    )
  )
)

_a-0000e61a-f20f-8000-9ba5-011c48011c48_.sail_code:
