#"SYSTEM_SYSRULES_richTextItem_v1"(
  text: #"SYSTEM_SYSRULES_forEach"(
    items: ri!responseRequirementAudits.simpleFieldChanges,
    expression: #"SYSTEM_SYSRULES_richTextBulletedList"(
      items: #"SYSTEM_SYSRULES_richTextListItem"(
        text: rule!AS_GCM_UT_constructAuditCommentForQuestionnaireTemplateFields(
          fieldName: fv!item.fieldName,
          oldValue: fv!item.oldValue,
          newValue: fv!item.newValue,
          bundle: ri!bundle
        )
      )
    )
  ),
  showWhen: rule!AS_CO_UT_isNotBlank(
    ri!responseRequirementAudits.simpleFieldChanges
  )
)

/*Precedent Question Template Added / Removed*/
#"SYSTEM_SYSRULES_richTextItem_v1"(
  text: {
    a!match(
      value: rule!AS_GCM_filterRecords(
        records: ri!questionPrecedentSetAudits[1].simpleFieldChanges,
        field: "fieldName",
        value: "operator"
      ),
      whenTrue: rule!AS_CO_UT_isBlank(fv!value),
      then: {},
      whenTrue: rule!AS_CO_UT_isBlank(fv!value.oldValue),
      whenTrue: rule!AS_CO_UT_isBlank(fv!value.newValue),
      then: a!localVariables(
        local!visibilityLabels: if(
          rule!AS_CO_UT_isBlank(fv!value.oldValue),
          {
            "txt_AlwaysVisible",
            "txt_VisibleOnConditions"
          },
          {
            "txt_VisibleOnConditions",
            "txt_AlwaysVisible"
          }
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            {
              "",
              char(32),
              rule!AS_GAM_CO_I18N_UT_displayLabel(
                bundleKey: "lbl_Visibility",
                bundle: ri!bundle
              )
            },
            char(32),
            "",
            char(32),
            {
              char(34),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!bundle,
                  bundleKey: local!visibilityLabels[1]
                )
              ),
              char(34)
            },
            char(32),
            "",
            char(32),
            {
              char(34),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!bundle,
                  bundleKey: local!visibilityLabels[2]
                )
              ),
              char(34)
            },
            char(10)
          },
          size: "STANDARD"
        )
      ),
      default: {}
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: "Conditional Logic Details",
          color: "SECONDARY",
          style: "STRONG"
        ),
        #"SYSTEM_SYSRULES_richTextBulletedList"(
          items: #"SYSTEM_SYSRULES_richTextListItem"(
            text: #"SYSTEM_SYSRULES_forEach"(
              items: ri!questionPrecedentSetAudits[1].simpleFieldChanges,
              expression: rule!AS_GCM_UT_constructAuditCommentForQuestionnaireTemplateFields(
                fieldName: fv!item.fieldName,
                oldValue: fv!item.oldValue,
                newValue: fv!item.newValue,
                bundle: ri!bundle
              )
            ),
            showWhen: rule!AS_CO_UT_isNotBlank(
              ri!questionPrecedentSetAudits[1].simpleFieldChanges
            )
          ),

        ),
        #"SYSTEM_SYSRULES_richTextBulletedList"(
          items: a!flatten(
            #"SYSTEM_SYSRULES_forEach"(
              items: ri!questionPrecedentSetAudits[1].questionPrecedentChanges,
              expression: a!localVariables(
                local!questionPrecedentAudit: fv!item,
                local!simpleFieldChanges: index(rule!AS_GCM_filterRecords(
                  records: local!questionPrecedentAudit.simpleFieldChanges,
                  field: "fieldName",
                  value: "questionTemplateId_precedent"
                ),1,{}),
                local!questionTemplateId: tointeger(
                  a!defaultValue(
                    value: index(
                      local!simpleFieldChanges,
                      "oldValue",
                      null
                    ),
                    default: index(
                      local!simpleFieldChanges,
                      "newValue",
                      null
                    )
                  )
                ),
                local!questions: index(
                  rule!AS_GCM_QNM_QR_getQuestionPrecedent(
                    questionPrecedentTemplateId: local!questionPrecedentAudit.questionPrecedentTemplateId,
                    questionTemplateId: local!questionTemplateId,
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
                    fields: recordType!AS GCM QNM T Question Precedent.questionTemplate.label
                  ),
                  recordType!AS GCM QNM T Question Precedent.questionTemplate,
                  null
                ),
                {
                  #"SYSTEM_SYSRULES_richTextListItem"(
                    text: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: {
                        {
                          /* Question changes */
                          rule!AS_GCM_DSP_displayAuditActionCodeText(
                            actionCode: local!questionPrecedentAudit.auditActionCode,
                            bundle: ri!bundle,
                            deleteLabel: "txt_Removed"
                          ),
                          " ",
                          if(
                            rule!AS_CO_UT_isNotBlank(local!questions),
                            local!questions[recordType!AS GCM QNM T Question.label],
                            {}
                          )
                        },
                        {
                          /* Conditional Logic Changes */
                          char(10),
                          rule!AS_GCM_DSP_displayModificationCommentForQuestionTemplatePrecedentResponses(
                            bundle: ri!bundle,
                            responseRequirementAudits: rule!AS_CO_UT_indexPath(
                              cdt: local!questionPrecedentAudit,
                              field: "responseRequirementChanges"
                            )
                          )
                        }
                      }
                    )
                  )
                }
              )
            )
          )
        )
      }
    )
  },
  showWhen: rule!AS_CO_UT_isNotBlank(ri!questionPrecedentSetAudits)
)

