                    questionCategoryTemplateAuditId: local!questionCategoryTemplateAuditIds,
                    questionCategories: ri!questionnaireTemplate[recordType!AS GCM QNM T Questionnaire.questionCategories],
                    questionCategoryAudit: local!questionCategoriesAudit
                  ),
                  /*Question Template Added / Removed/ Updated*/
                  rule!AS_GCM_DSP_displayModificationCommentForQuestionTemplate(
                    bundle: ri!bundle,
                    questionTemplateAuditId: local!questionTemplateAuditIds,
                    questionTemplate: ri!questionnaireTemplate[recordType!AS GCM QNM T Questionnaire.questions],
                    questionTemplateAudit: local!questionTemplateAudit,
                    questionCategory: ri!questionnaireTemplate[recordType!AS GCM QNM T Questionnaire.questionCategories]
                  )
                }
              },
              {}
            )
          )
        }
      )
    },
    showWhen: or(
      rule!AS_CO_UT_isNotBlank(
        ri!questionnaireTemplateAudit.simpleFieldChanges
      ),
      rule!AS_CO_UT_isNotBlank(local!questionCategoryTemplateAuditIds),
      rule!AS_CO_UT_isNotBlank(local!questionTemplateAuditIds)
    )
  )
)

/*Question Template Added / Removed*/
a!localVariables(
  local!questionTemplatesAudit: if(
    count(ri!questionTemplateAudit) > 4,
    index(
      ri!questionTemplateAudit,
      enumerate(4) + 1,
      null
    ),
    ri!questionTemplateAudit
  ),
  /*Showing only first four Question Template Audit*/
  local!showLess: if(
    count(ri!questionTemplateAudit) > 4,
    true,
    false
  ),
  local!size,
  /*richTextDisplayField included in parent rule*/
  #"SYSTEM_SYSRULES_richTextItem_v1"(
    text: {
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: "Question Details",
        color: "SECONDARY",
        style: "STRONG"
      ),
      #"SYSTEM_SYSRULES_richTextBulletedList"(
        items: {
          #"SYSTEM_SYSRULES_forEach"(
            items: local!questionTemplatesAudit,
            expression: #"SYSTEM_SYSRULES_richTextListItem"(
              text: a!localVariables(
                local!questionTemplateAudit: fv!item,
                local!questionTemplates: index(
                  ri!questionTemplate,
                  wherecontains(
                    tointeger(
                      local!questionTemplateAudit.questionTemplateId
                    ),
                    tointeger(
                      ri!questionTemplate[recordType!AS GCM QNM T Question.questionTemplateId]
                    )
                  ),
                  {}
                ),
                local!isQuestionDeleted: rule!AS_CO_UT_isBlank(local!questionTemplates),
                local!deletedQuestion: if(
                  local!isQuestionDeleted,
                  rule!AS_GCM_QNM_QR_getQuestion_Template(
                    questionTemplateId: local!questionTemplateAudit.questionTemplateId,
                    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
                    fields: {
                      recordType!AS GCM QNM T Question.label,
                      recordType!AS GCM QNM T Question.questionCategoryTemplateId
                    },
                    isActive: { true, false }
                  ),
                  {}
                ),
                local!category: if(
                  local!questionTemplateAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
                  rule!AS_GCM_QNM_QR_getQuestionCategories(
                    questionCategoryTemplateId: index(
                      rule!AS_CO_UT_rejectNullValuesFromArray(
                        array: {
                          rule!AS_CO_UT_indexPath(
                            cdt: local!questionTemplateAudit,
                            field: "simpleFieldChanges"
                          ).oldValue,
                          rule!AS_CO_UT_indexPath(
                            cdt: local!questionTemplateAudit,
                            field: "simpleFieldChanges"
                          ).newValue
                        }
                      ),
                      wherecontains(
                        "questionCategoryTemplateId",
                        rule!AS_CO_UT_indexPath(
                          cdt: local!questionTemplateAudit,
                          field: "simpleFieldChanges"
                        ).fieldName
                      )
                    )
                  ),
                  if(
                    local!isQuestionDeleted,
                    rule!AS_GCM_QNM_QR_getQuestionCategories(
                      questionCategoryTemplateId: tointeger(
                        local!deletedQuestion[recordType!AS GCM QNM T Question.questionCategoryTemplateId]
                      )
                    ),
                    index(
                      ri!questionCategory,
                      wherecontains(
                        tointeger(
                          local!questionTemplates[recordType!AS GCM QNM T Question.questionCategoryTemplateId]
                        ),
                        tointeger(
                          ri!questionCategory[recordType!AS GCM QNM T Question Category.questionCategoryTemplateId]
                        )
                      ),
                      {}
                    )
                  )
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    rule!AS_GCM_DSP_displayAuditActionCodeText(
                      actionCode: local!questionTemplateAudit.auditActionCode,
                      bundle: ri!bundle,
                      deactivateLabel: "txt_Removed"
                    ),
                    " ",
                    if(
                      rule!AS_CO_UT_isNotBlank(
                        local!questionTemplates[recordType!AS GCM QNM T Question.label]
                      ),
                      local!questionTemplates[recordType!AS GCM QNM T Question.label],
                      if(
                        local!isQuestionDeleted,
                        index(
                          local!deletedQuestion,
                          recordType!AS GCM QNM T Question.label,
                          ""
                        ),
                        index(
                          if(
                            local!questionTemplateAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
                            rule!AS_CO_UT_indexPath(
                              cdt: local!questionTemplateAudit,
                              field: "simpleFieldChanges"
                            ).oldValue,
                            rule!AS_CO_UT_indexPath(
                              cdt: local!questionTemplateAudit,
                              field: "simpleFieldChanges"
                            ).newValue
                          ),
                          wherecontains(
                            "label",
                            rule!AS_CO_UT_indexPath(
                              cdt: local!questionTemplateAudit,
                              field: "simpleFieldChanges"
                            ).fieldName
                          ),

                        )
                      )
                    ),
                    char(32),
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: {
                        if(
                          local!questionTemplateAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
                          "",
                          if(
                            local!questionTemplateAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
                            "in",
                            ""
                          )
                        ),
                        char(32),
                        ""
                      },
                      style: "STRONG"
                    ),
                    char(32),
                    local!category[recordType!AS GCM QNM T Question Category.questionCategoryName],
                    #"SYSTEM_SYSRULES_forEach"(
                      items: local!questionTemplateAudit.simpleFieldChanges,
                      expression: if(
                        rule!AS_CO_UT_isNotBlank(
                          rule!AS_GCM_UT_displayFieldNamesForQuestionnaireTemplateAuditHistory(fieldName: fv!item.fieldName)
                        ),
                        #"SYSTEM_SYSRULES_richTextBulletedList"(
                          items: {
                            #"SYSTEM_SYSRULES_richTextListItem"(
                              text: rule!AS_GCM_UT_constructAuditCommentForQuestionnaireTemplateFields(
                                fieldName: fv!item.fieldName,
                                oldValue: fv!item.oldValue,
                                newValue: fv!item.newValue,
                                bundle: ri!bundle
                              )
                            )
                          },
                          showWhen: and(
                            local!questionTemplateAudit.auditActionCode = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
                            rule!AS_CO_UT_isNotBlank(
                              local!questionTemplateAudit.simpleFieldChanges
                            )
                          )
                        ),
                        {}
                      )
                    ),
                    /*Precedent Question Template Added / Removed/ Updated*/
                    #"SYSTEM_SYSRULES_richTextBulletedList"(
                      items: rule!AS_GCM_DSP_displayModificationCommentForQuestionTemplatePrecedents(
                        bundle: ri!bundle,
                        questionPrecedentSetAudits: local!questionTemplateAudit.isDisplayedPrecedentSetsChanges
                      )
                    )
                  }
                )
              )
            )
          ),

        }
      ),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: if(
          local!showLess = true,
          "",
          ""
        ),
        link: #"SYSTEM_SYSRULES_dynamicLink"(
          saveInto: {
            a!save(
              local!showLess,
              if(local!showLess = true, false, true)
            ),
            a!save(
              local!questionTemplatesAudit,
              if(
                local!showLess = false,
                ri!questionTemplateAudit,
                index(
                  ri!questionTemplateAudit,
                  enumerate(4) + 1,
                  null
                )
              )
            )
          }
        ),
        linkStyle: "STANDALONE",
        showWhen: count(ri!questionTemplateAudit) > 4,
        size: rule!AS_CO_UT_replaceNull(
          nullableValue: local!size,
          replacementValue: "STANDARD"
        )
      )
    },
    showWhen: rule!AS_CO_UT_isNotBlank(ri!questionTemplateAuditId)
  )
)

