          relationship: recordType!AS GCM QNM T Question.isDisplayedPrecedentSets,
          limit: 100
        ),
        #"SYSTEM_SYSRULES_relatedRecordData"(
          relationship: #"urn:appian:record-relationship:v1:66fc3179-f0d8-4d6d-bbce-92b85f7c665a/35500f1d-7bad-40e6-8728-6bd75b0d3f03/8dca9ba9-cfbd-46c6-92b8-6a7d21aad4c4/b37fc648-4c9c-4add-b535-2cbb861e4e19",
