)

_a-0000e6e4-7cee-8000-9bde-011c48011c48_.sail_code:
a!match(
  /* changes to below fields will be shown as Added/Removed when the oldValue/newValue is null */
  value: {
    "operator",
    "requirementTypeCode",
    "requirementValue"
  },
  /* Added */
  whenTrue: and(
    rule!AS_CO_UT_isBlank(ri!oldValue),
    contains(fv!value, ri!fieldName)
  ),
  then: #"SYSTEM_SYSRULES_richTextItem_v1"(
    text: {
      rule!AS_GCM_DSP_displayAuditActionCodeText(
        actionCode: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_ADD,
        bundle: ri!bundle
      ),
      char(32),
      rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundleKey: rule!AS_GCM_UT_displayFieldNamesForQuestionnaireTemplateAuditHistory(fieldName: ri!fieldName),
        bundle: ri!bundle
      ),
      char(32),
      rule!AS_GCM_DSP_displayAuditModificationForQuestionnaireTemplate(
        value: ri!newValue,
        fieldName: ri!fieldName,
        bundle: ri!bundle
      )
    },
    size: "STANDARD"
  ),
  /* Deleted */
  whenTrue: and(
    rule!AS_CO_UT_isBlank(ri!newValue),
    contains(fv!value, ri!fieldName)
  ),
  then: #"SYSTEM_SYSRULES_richTextItem_v1"(
    text: {
      rule!AS_GCM_DSP_displayAuditActionCodeText(
        actionCode: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_DELETE,
        bundle: ri!bundle,
        deleteLabel: "txt_Removed"
      ),
      char(32),
      rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundleKey: rule!AS_GCM_UT_displayFieldNamesForQuestionnaireTemplateAuditHistory(fieldName: ri!fieldName),
        bundle: ri!bundle
      ),
      char(32),
      rule!AS_GCM_DSP_displayAuditModificationForQuestionnaireTemplate(
        value: ri!oldValue,
        fieldName: ri!fieldName,
        bundle: ri!bundle
      )
    },
    size: "STANDARD"
  ),
  /* Modified */
  default: #"SYSTEM_SYSRULES_richTextItem_v1"(
    text: {
      {
        "",
        char(32),
        rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundleKey: rule!AS_GCM_UT_displayFieldNamesForQuestionnaireTemplateAuditHistory(fieldName: ri!fieldName),
          bundle: ri!bundle
        ),

      },
      char(32),
      "",
      char(32),
      if(
        rule!AS_CO_UT_isBlank(ri!oldValue),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_GAM_CO_I18N_UT_displayLabel(bundleKey: "txt_Blank", bundle: ri!bundle),
          color: "SECONDARY"
        ),
        {
          char(34),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_GCM_DSP_displayAuditModificationForQuestionnaireTemplate(
              bundle: ri!bundle,
              value: ri!oldValue,
              fieldName: ri!fieldName
            )
          ),
          char(34)
        }
      ),
      char(32),
      "",
      char(32),
      if(
        rule!AS_CO_UT_isBlank(ri!newValue),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: rule!AS_GAM_CO_I18N_UT_displayLabel(bundleKey: "txt_Blank", bundle: ri!bundle),
          color: "SECONDARY"
        ),
        {
          char(34),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: rule!AS_GCM_DSP_displayAuditModificationForQuestionnaireTemplate(
              bundle: ri!bundle,
              value: ri!newValue,
              fieldName: ri!fieldName
            )
          ),
          char(34)
        }
      ),

    },
    size: "STANDARD"
  )
)

