_a-0000e67d-efac-8000-9bcd-011c48011c48_.sail_code:
if(
  all(
    rule!AS_CO_UT_isBlank,
    { ri!refClauses, ri!refClauseTexts }
  ),
  rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_R_Clause_Helper'
  ),
  /*Combining AS_GCM_R_Clause and AS_GCM_R_Clause_Texts*/
  a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!refClauses,
      expression: rule!AS_GCM_UT_nullifyEmptyNestedCdtFields(
        cdt: rule!AS_CO_UT_cast(
          type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_R_Clause_Helper',
          value: {
            clauseId: fv!item[recordType!AS GCM R Clause.clauseId],
            clauseNumber: fv!item[recordType!AS GCM R Clause.clauseNumber],
            clauseTitle: fv!item[recordType!AS GCM R Clause.clauseTitle],
            alternateValue: fv!item[recordType!AS GCM R Clause.alternateValue],
            effectiveDate: fv!item[recordType!AS GCM R Clause.effectiveDate],
            ucfSection: recordType!AS GCM R Section(
              recordType!AS GCM R Section.sectionId: fv!item[recordType!AS GCM R Clause.ucfSectionId]
            ),
            refSource: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.refSourceId]
            ),
            refType: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.refTypeId]
            ),
            refFormat: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.refFormatId]
            ),
            fillIn: fv!item[recordType!AS GCM R Clause.fillIn],
            editable: fv!item[recordType!AS GCM R Clause.editable],
            prescriptionNumber: fv!item[recordType!AS GCM R Clause.prescriptionNumber],
            fpSup: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.fpSupId]
            ),
            crSup: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.crSupId]
            ),
            fpRd: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.fpRdId]
            ),
            crRd: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.crRdId]
            ),
            fpSvc: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.fpSvcId]
            ),
            crSvc: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.crSvcId]
            ),
            fpCon: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.fpConId]
            ),
            crCon: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.crConId]
            ),
            tmLh: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.tmLhId]
            ),
            lmv: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.lmvId]
            ),
            comSvc: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.comSvcId]
            ),
            ddr: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.ddrId]
            ),
            ae: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.aeId]
            ),
            fac: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.facId]
            ),
            indDel: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.indDelId]
            ),
            trn: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.trnId]
            ),
            sap: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.sapId]
            ),
            utlSvc: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.utlSvcId]
            ),
            cpCs: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.cpCsId]
            ),
            prescriptionText: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!refClauseTexts,
              field: "clauseId",
              value: fv!item[recordType!AS GCM R Clause.clauseId]
            ).prescriptionText,
            clauseText: rule!AS_CO_UT_filterCdtByField(
              cdt: ri!refClauseTexts,
              field: "clauseId",
              value: fv!item[recordType!AS GCM R Clause.clauseId]
            ).clauseText,
            /*Metadata from both the CDTs would be the same. Using AS_GCM_R_Clause's data here*/
            syncUpdatedBy: fv!item[recordType!AS GCM R Clause.syncUpdatedBy],
            syncUpdatedDatetime: fv!item[recordType!AS GCM R Clause.syncUpdatedDatetime],
            createdBy: fv!item[recordType!AS GCM R Clause.createdBy],
            createdDatetime: fv!item[recordType!AS GCM R Clause.createdDatetime],
            modifiedBy: fv!item[recordType!AS GCM R Clause.modifiedBy],
            modifiedDatetime: fv!item[recordType!AS GCM R Clause.modifiedDatetime],
            isActive: fv!item[recordType!AS GCM R Clause.isActive]
          }
        )
      )
    )
  )
)

_a-0000e615-fd0f-8000-9bc1-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS GCM R Clause,
  returnType: a!defaultValue(
    value: ri!returnType,
    default: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregationFields
        }
      ),
      rule!AS_CO_UT_isNotBlank(ri!clauseIds),
      rule!AS_CO_UT_isNotBlank(ri!clauseNumbers),
      ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_TOTAL_COUNT
    )
  ),
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS GCM R Clause.refFormat,
          recordType!AS GCM R Clause.ucfSection,
          recordType!AS GCM R Clause.refSource,
          recordType!AS GCM R Clause.refType,
          recordType!AS GCM R Clause.additionalSections
        },
        {}
      )
    }
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.clauseId,
      operator: "in",
      value: ri!clauseIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.clauseNumber,
      operator: "in",
      value: ri!clauseNumbers
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.clauseNumberWithAlternate,
      operator: "in",
      value: #"SYSTEM_SYSRULES_forEach"(
        items: ri!alternateValues,
        expression: joinarray({ ri!clauseNumbers[1], fv!item }, " | ")
      ),
      applyWhen: and(
        /*  For Efficiency we enfore the ri!clauseNumbers value to be exactly 1     */
        rule!AS_GAM_UT_ArrayLength(ri!clauseNumbers) = 1,
        rule!AS_CO_UT_isNotBlank(ri!alternateValues)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.deviation,
      operator: "=",
      value: false,
      applyWhen: rule!AS_CO_UT_booleanDefaultFalse(ri!excludeDeviations)
    ),
    ri!additionalFilters
  },
  aggregationFields: ri!aggregationFields,
  relatedRecordData: ri!relatedRecordData
)

