_a-0000eea8-0b3f-8000-9e48-011c48011c48_.sail_code:
a!localVariables(
  /* Determine whether to execute the _default or _custom rule */
  local!runtimeRule: a!refreshVariable(
    value: rule!AS_FRM_determineRuntimeRule(
      defaultRuleReference: rule!AS_GCM_AIFillInFeatureEnabled_v1_default
    ),
    refreshAlways: true
  ),
  /*Setting Default as true in case of invalid toggle value*/
  if(
    rule!AS_CO_UT_typeCompare(
      value1: local!runtimeRule(),
      value2: true()
    ),
    local!runtimeRule(),
    true
  )
)

_a-0000e8e5-7677-8000-9c1d-011c48011c48_.sail_code:
a!localVariables(
  local!selectedTab: 1,
  local!triggerValidation: false(),
  /* This local and local!originalClauseText will not get refresh as the clauseTextDetails and clauseDetails would be changed only when the form is saved */
  local!clauseTextAndFillinMaps: rule!AS_GCM_getFillinMapsAndClauseTextUsingHighlightOrCiteTags(
    clauseText: ri!refClauseText.clauseText,
    clauseNumber: ri!refClause[recordType!AS GCM R Clause.clauseNumber],
    isConvertCiteToHighlight: true(),
    getFillinRecommendationsFromDB: true(),
    isIgnoreTables: false,
    fillinMaps: ri!tempFillInMaps
  ),
  local!originalClauseText: index(
    local!clauseTextAndFillinMaps,
    "updatedClauseText",
    ""
  ),
  /* Initializing the value in the beginning */
  local!runTimeClauseText: if(
    rule!AS_GCM_doesHtmlContainVisibleCharacters(text: local!originalClauseText),
    local!originalClauseText,
    ""
  ),
  local!fillinMaps: if(
    rule!AS_CO_UT_isBlank(ri!tempFillInMaps),
    index(
      local!clauseTextAndFillinMaps,
      "fillinMaps",
      {}
    ),
    ri!tempFillInMaps
  ),
  local!existingFillinMaps: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_forEach"(
      items: local!fillinMaps,
      expression: #"urn:appian:function:v1:a:update"(
        fv!item,
        "fillinRecommendations",
        rule!AS_GCM_filterRecords(
          records: fv!item.fillinRecommendations,
          field: recordType!AS GCM Fill In Recommendation.fillInRecId,
          value: null,
          filterType: "REJECT"
        )
      )
    ),
    refreshOnReferencedVarChange: false()
  ),
  local!fillinTypes: index(local!fillinMaps, "fillinType", {}),
  local!clauseTextInRTE: a!refreshVariable(
    value: local!runTimeClauseText,
    refreshOnReferencedVarChange: false()
  ),
  local!isSaveDisable: a!refreshVariable(
    value: if(
      substitute(
        local!runTimeClauseText,
        "<table >",
        "<table>"
      ) = substitute(
        local!clauseTextInRTE,
        "<table >",
        "<table>"
      ),
      false(),
      true()
    ),
    refreshOnReferencedVarChange: true()
  ),
  local!allowSubmit: and(
    a!isNotNullOrEmpty(
      ri!refClause[recordType!AS GCM R Clause.prescriptionNumber]
    ),
    /*Checking if the fillinTypes don't have any null*/
    rule!AS_CO_UT_isBlank(
      value: rule!AS_CO_UT_whereContains(values: null(), array: local!fillinTypes)
    ),
    rule!AS_GCM_doesHtmlContainVisibleCharacters(text: ri!refClauseText.prescriptionText),
    rule!AS_GCM_doesHtmlContainVisibleCharacters(text: local!runTimeClauseText)
  ),
  #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      rule!AS_GCM_COL_clauseTextTabs(
        bundle: ri!bundle,
        selectedTab: local!selectedTab,
        triggerValidation: local!triggerValidation
      ),
      #"SYSTEM_SYSRULES_cardLayout"(
        padding: "NONE",
        showBorder: false,
        showShadow: true()
      ),
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: {
          choose(
            local!selectedTab,
            /*CLAUSE TEXT SECTION*/
            rule!AS_GCM_CPS_clauseTextDetails(
              bundle: ri!bundle,
              clause: ri!refClause,
              runTimeClauseText: local!runTimeClauseText,
              clauseTextInRTE: local!clauseTextInRTE,
              fillinMaps: local!fillinMaps,
              confirmationBannerShowWhen: local!isSaveDisable,
              refFillinRecType: ri!refFillinRecType,
              refClauseSetType: ri!refClauseSetType,
              isIdentifyFillInWithAI: ri!isIdentifyFillInWithAI,
              clauseTextForAI: ri!clauseTextForAI,
              tempFillInMaps: ri!tempFillInMaps,
              aiIdentifiedFillInsUuids: ri!aiIdentifiedFillInsUuids,
              isCreate: ri!isCreate
            ),
            /*Prescription Text*/
            rule!AS_GCM_CPS_clausePrescriptionDetails(
              bundle: ri!bundle,
              clause: ri!refClause,
              refClauseText: ri!refClauseText
            )
          ),
          /* Validation banner cards for Prescription number/text & fill-in types */
          rule!AS_GCM_CPS_showValidationBannerCards(
            prescriptionNumber: ri!refClause[recordType!AS GCM R Clause.prescriptionNumber],
            prescriptionText: ri!refClauseText.prescriptionText,
            triggerValidation: local!triggerValidation,
            bundle: ri!bundle,
            fillinTypes: local!fillinTypes,
            selectedTab: local!selectedTab,
            clauseText: local!clauseTextInRTE
          )
        },
        showBorder: false,
        showShadow: false()
      ),
      #"SYSTEM_SYSRULES_cardLayout"(
        contents: rule!AS_GCM_DPL_CO_CPS_cancelSaveBackNextButtons(
          /* Add or save button */
          confirmLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
            bundle: ri!bundle,
            bundleKey: if(ri!isCreate, "btn_Add", "btn_Save")
          ),
          /*Cancel button*/
          cancelLabel: "Cancel",
          confirmSaveInto: {
            if(
              local!allowSubmit <> true(),
              a!save(local!triggerValidation, true()),
              {
                a!save(
                  ri!userAction,
                  cons!AS_CO_ENUM_USER_ACTION_SAVE
                ),
                a!save(
                  ri!refClauseText.isActive,
                  ri!refClause[recordType!AS GCM R Clause.isActive]
                ),
                a!save(
                  ri!refClauseText.clauseText,
                  /* Removing the span hidden tags if any */
                  if(
                    local!runTimeClauseText = local!originalClauseText,
                    ri!refClauseText.clauseText,
                    rule!AS_GCM_replaceHighlightTagWithCiteTag(
                      clauseText: local!runTimeClauseText,
                      highlightTagText: index(local!fillinMaps, "highlightTag", null()),
                      citeTagText: index(local!fillinMaps, "citeTag", null()),
                      isRemoveCiteFromTables: false()
                    )
                  )
                ),
                if(
                  rule!AS_GCM_IsClauseTextContainsCiteTags(
                    clauseText: ri!refClauseText.clauseText,
                    ignoreGrids: false()
                  ) = true(),
                  a!save(
                    ri!refClause[recordType!AS GCM R Clause.fillIn],
                    true
                  ),
                  null
                ),
                a!save(
                  ri!deletedFillinRecommendations,
                  rule!AS_CO_UT_rejectNullValuesFromArray(
                    array: {
                      /* Removing the deleted fillin recommendations */
                      rule!AS_GAM_rejectCdtOrRecordDataByField(
                        cdtOrRecord: index(
                          local!existingFillinMaps,
                          "fillinRecommendations",
                          {}
                        ),
                        field: recordType!AS GCM Fill In Recommendation.fillInRecId,
                        value: rule!AS_CO_UT_rejectNullValuesFromArray(
                          array: index(
                            index(
                              local!fillinMaps,
                              "fillinRecommendations",
                              {}
                            ),
                            recordType!AS GCM Fill In Recommendation.fillInRecId,
                            {}
                          )
                        ),
                        rejectWhen: rule!AS_CO_UT_isNotBlank(
                          input: index(
                            index(
                              local!fillinMaps,
                              "fillinRecommendations",
                              {}
                            ),
                            recordType!AS GCM Fill In Recommendation.fillInRecId,
                            {}
                          )
                        )
                      ),
                      /* Removing the recommendations if the fill-in type is checkbox */
                      index(
                        rule!AS_CO_UT_filterCdtByField(
                          cdt: local!fillinMaps,
                          field: "fillinType",
                          value: cons!AS_GCM_TXT_FILL_IN_TYPE_CHECKBOX
                        ),
                        "fillinRecommendations",
                        {}
                      )
                    }
                  )
                ),
                a!save(
                  ri!fillinRecommendations,
                  /* Saving the new/updated fillin recommendations */
                  rule!AS_CO_UT_rejectNullValuesFromArray(
                    array: difference(
                      rule!AS_CO_UT_cast(
                        type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                          recordType!AS GCM Fill In Recommendation
                        ),
                        value: rule!AS_GAM_rejectCdtOrRecordDataByField(
                          cdtOrRecord: index(
                            local!fillinMaps,
                            "fillinRecommendations",
                            {}
                          ),
                          field: recordType!AS GCM Fill In Recommendation.fillInRecId,
                          value: index(
                            ri!deletedFillinRecommendations,
                            recordType!AS GCM Fill In Recommendation.fillInRecId,
                            {}
                          ),
                          rejectWhen: rule!AS_CO_UT_isNotBlank(
                            input: index(
                              ri!deletedFillinRecommendations,
                              recordType!AS GCM Fill In Recommendation.fillInRecId,
                              {}
                            )
                          )
                        )
                      ),
                      /* Differencing it with the existing/deleted recommendations to ignore the unchanged/ removed recommendations */
                      rule!AS_CO_UT_cast(
                        type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                          recordType!AS GCM Fill In Recommendation
                        ),
                        value: {
                          index(
                            local!existingFillinMaps,
                            "fillinRecommendations",
                            {}
                          ),
                          ri!deletedFillinRecommendations
                        }
                      )
                    )
                  )
                ),
                if(
                  ri!isCreate = true(),
                  rule!AS_GCM_MTR_SAVE_createClause(),
                  rule!AS_GCM_MTR_SAVE_editClause()
                )
              }
            )
          },
          cancelSaveInto: a!save(
            ri!userAction,
            cons!AS_CO_ENUM_USER_ACTION_CANCEL
          ),
          /* Divider */
          includeDivider: true(),
          confirmSubmit: local!allowSubmit,
          cancelSubmit: true,
          confirmDisabled: local!isSaveDisable,
          confirmStyle: "SOLID",
          cancelStyle: "LINK",
          cancelShowWhen: true(),
          /* Back Button */
          backLabel: "Back",
          backSaveInto: {
            a!save(ri!formStepNumber, ri!formStepNumber - 1)
          },
          backStyle: "OUTLINE",
          backShowWhen: true(),
          /* Next Button */
          nextShowWhen: false(),
          confirmShowWhen: true(),
          divider: "ABOVE",
          confirmTooltip: if(
            local!isSaveDisable = true(),
            "Please confirm changes made to the clause before saving",
            null()
          )
        ),
        showBorder: false()
      ),

    },
    showWhen: ri!showWhen,
    shape: "SEMI_ROUNDED",
    padding: "NONE",
    marginAbove: "EVEN_LESS"
  )
)

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
/*Do a "fuzzy" comparison where all we care about is the data*/
/*This will match a list of variant/dictionary/cdt if their fields have the same values*/
cast(
   'type!{http://www.appian.com/ae/types/2009}Boolean',
  tostring(ri!value1) = tostring(ri!value2)
)

_a-0000e61a-f20f-8000-9ba5-011c48011c48_.sail_code:
