_a-0000e447-933d-8000-9baa-011c48011c48_.sail_code:
if(
/* to prevent modulus from breaking */
  isnull(ri!value),
  ri!value,
  if(
    todecimal(ri!value) = tostring(ri!value),
    if(
      mod(ri!value, 1) = 0,
      text(
        todecimal(ri!value), "##"
      ),
      text(
        todecimal(ri!value), "##.##"
      )
    ),
    ri!value
  )
)

_a-0000e2ad-62e7-8000-9ba2-011c48011c48_.sail_code:
cast(
