a!localVariables(
  local!index: find("rgb", ri!tag),
  local!colIndex: if(
    ri!isBackgroundColor = false,
    find("rgb", ri!tag, local!index + 3),
    null
  ),
  local!hexdec: if(
    ri!isBackgroundColor = false,
    index(
      split(
        mid(
          ri!tag,
          find("color:", ri!tag, local!index + 3),
          14
        ),
        "#"
      ),
      2,
      {}
    ),
    index(
      split(
        mid(ri!tag, find("color:", ri!tag), 14),
        "#"
      ),
      2,
      {}
    )
  ),
  local!color: mid(
    ri!tag,
    if(
      ri!isBackgroundColor = false,
      local!colIndex,
      local!index
    ),
    18
  ),
  local!rgbCodes: tointeger(split(local!color, ", ")),
  if(
    rule!AS_CO_UT_isBlank(local!rgbCodes),
    local!hexdec,
    concat(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!rgbCodes,
        expression: dec2hex(fv!item, 2)
      )
    )
  )
)

