/*Returns the fillins map for the clauseText*/
if(
  rule!AS_CO_UT_isNotBlank(input: ri!clauseText),
  a!localVariables(
    local!anchorTags: regexallmatches(
      "<a class(.*?)>(.*?)</a>",
      ri!clauseText
    ),
    local!replaceText: #"SYSTEM_SYSRULES_forEach"(
      items: local!anchorTags,
      expression: if(
        find("http", fv!item) > 0,
        fv!item,
        striphtml(fv!item)
      )
    ),
    /*Removing self referencing hyperlinks*/
    local!clauseText: rule!AS_GCM_substituteListFromTextUsingReduce(
      text: ri!clauseText,
      find: local!anchorTags,
      replaceWith: local!replaceText
    ),
    local!clauseTextIgnoringTables: if(
      ri!isIgnoreTables = false(),
      local!clauseText,
      rule!AS_GCM_UT_removeCiteAndHighlightTagWithinTables(clauseText: local!clauseText)
    ),
    local!citeTags: if(
      ri!isConvertCiteToHighlight = true(),
      regexallmatches(
        "<cite(.*?)</cite>",
        local!clauseTextIgnoringTables
      ),
      {}
    ),
    local!highLightTags: if(
      ri!isConvertCiteToHighlight = true(),
      #"SYSTEM_SYSRULES_forEach"(
        items: local!citeTags,
        expression: a!localVariables(
          local!class: regexfirstmatch("class=""(.*?)""", fv!item),
          local!value: regexreplaceall("<cite(.*?)>|</cite>", fv!item, ""),
          concat(
            "<span style=""background-color: " & a!defaultValue(
              value: ri!highlightColorCode,
              default: cons!AS_GCM_HEX_CODE_DEFAULT_FILL_IN_HIGHLIGHT_IN_ADD_OR_EDIT_CLAUSE
            ) & ";"">",
            "<span hidden="""">",
            local!class,
            "</span>",
            local!value,
            "</span>"
          )
        )
      ),
      rule!AS_CO_UT_rejectNullValuesFromArray(
        array: #"SYSTEM_SYSRULES_forEach"(
          items: rule!AS_GCM_extractBalancedTagThatHasGivenText(
            text: local!clauseTextIgnoringTables,
            uniqueTextInTag: "background-color:"
          ),
          expression: if(
            /*Ignoring the transparent background-color tags*/
            or(
              cons!AS_GCM_HEX_CODE_HIGHLIGHT_TO_EXCLUDE_FROM_FILLINS = "#" & stripwith(
                rule!AS_GCM_UT_findColorCodes(tag: fv!item, isBackgroundColor: true()),
                ";"
              ),
              find(
                "background-color: transparent;",
                fv!item
              ) <> 0,
              if(
                ri!skipFillinForNewHighlights = true(),
                find("class=""cite", fv!item) = 0,
                {}
              )
            ),
            "",
            fv!item
          )
        )
      )
    ),
    local!fillinMaps: #"SYSTEM_SYSRULES_forEach"(
      items: merge(
        a!defaultValue(
          value: local!citeTags,
          default: repeat(length(local!highLightTags), "")
        ),
        local!highlightTags
      ),
      expression: a!localVariables(
        local!tables: regexallmatches("<table.*?</table>", local!clauseText),
        local!value: rule!AS_GCM_UT_removeHightlightTags(fv!item[2]),
        local!highlightTag: rule!AS_GCM_generateOrRetainClassParameterInHighlightOrCiteTag(
          highlightTag: fv!item[2],
          clauseNumber: ri!clauseNumber
        ),
        local!uuidAndfillinTypeMap: rule!AS_GCM_extractUuidAndFillinTypeFromClassParameter(
          citeOrHighlightTag: local!highlightTag,
          tables: if(ri!isIgnoreTables, null, local!tables)
        ),
        rule!AS_GCM_mergeMultipleMapsIntoSingleMap(
          mapsToMerge: {
            local!uuidAndfillinTypeMap,
            a!map(
              citeTag: a!defaultValue(
                value: fv!item[1],
                default: rule!AS_GCM_UT_generateCiteTagForFillins(
                  clauseNumber: ri!clauseNumber,
                  uuid: local!uuidAndfillinTypeMap.uuid,
                  fillinType: local!uuidAndfillinTypeMap.fillinType,
                  value: local!value
                )
              ),
              highlightTag: local!highlightTag,
              displayText: striphtml(local!value),
              fillinRecommendations: rule!AS_CO_UT_cast(
                type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                  recordType!AS GCM Fill In Recommendation
                ),
                value: {}
              )
            )
          }
        )
      )
    ),
    local!fillinRecommendations: rule!AS_GCM_QR_getFillInRecDetails(
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 5000),
      fillInRecFillInUuid: index(local!fillinMaps, "uuid", null),
      executeWhen: ri!getFillinRecommendationsFromDB = true()
    ).data,
    /* Updating the fillin Recommendations in the fillinMaps   */
    local!updatedFillinMaps: #"SYSTEM_SYSRULES_forEach"(
      items: local!fillinMaps,
      expression: #"urn:appian:function:v1:a:update"(
        fv!item,
        "fillinRecommendations",
        a!flatten(
          a!defaultValue(
            value: rule!AS_GAM_filterCdtOrRecordDataByField(
              cdtOrRecord: ri!fillinMaps,
              field: "uuid",
              value: fv!item.uuid
            ).fillinRecommendations,
            default: rule!AS_GAM_filterCdtOrRecordDataByField(
              cdtOrRecord: local!fillinRecommendations,
              field: recordType!AS GCM Fill In Recommendation.fillInRecFillInUuid,
              value: fv!item.uuid
            ),
            default: rule!AS_CO_UT_cast(
              type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
                recordType!AS GCM Fill In Recommendation
              ),
              value: {}
            )
          )
        )
      )
    ),
    a!map(
      fillinMaps: local!updatedFillinMaps,
      updatedClauseText: if(
        ri!isConvertCiteToHighlight = true(),
        rule!AS_GCM_substituteListFromTextUsingReduce(
          text: local!clauseText,
          find: index(local!fillinMaps, "citeTag", null),
          replaceWith: index(local!fillinMaps, "highlightTag", null)
        ),
        rule!AS_GCM_substituteListFromTextUsingReduce(
          text: local!clauseText,
          find: local!highLightTags,
          replaceWith: index(local!fillinMaps, "highlightTag", null)
        )
      )
    )
  ),
  a!map(fillinMaps: {}, updatedClauseText: "")
)

