        ),
        rule!AS_CO_UT_triggerRefresh(triggerRefresh: local!triggerRefresh)
      },
      cancelSaveInto: {
        a!save(
          ri!selectedFillinUuidsForRecommendations,
          remove(
            ri!selectedFillinUuidsForRecommendations,
            wherecontains(
              ri!fillinMap.uuid,
              ri!selectedFillinUuidsForRecommendations
            )
          )
        ),
        a!save(local!isReadOnly, true),
        a!save(
          ri!isAddRecOrAutoPopulateReadOnlyMaps[wherecontains(
