  local!isSyncProcess: a!defaultValue(value: ri!isSyncProcess, default: false()),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!clauses,
    expression:  rule!AS_GCM_setRecordFields(
      record: fv!item,
      neverOverwrite: {
        a!map(
          field: recordType!AS GCM R Clause.createdBy,
          value: ri!initiator
        ),
        a!map(
          field: recordType!AS GCM R Clause.createdDatetime,
          value: now()
        ),
        a!map(
          field: recordType!AS GCM R Clause.isActive,
          value: true()
        )
