_a-0000e915-02bc-8000-9c1f-011c48011c48_.sail_code:
if(
  search("<table", ri!clauseText) = 0,
  ri!clauseText,
  a!localVariables(
    /* split the text with table tag */
    local!tableSplit: split(ri!clauseText, { "<table", "</table>" }),
    joinarray(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!tableSplit,
        expression: a!localVariables(
          local!item: if(
            or(fv!isFirst, fv!isLast),
            fv!item,
            "<table" & fv!item & "</table>"
          ),
          if(
            search("<tbody", local!item) <> 0,
            if(
              /* searching for <cite or <ignore-cite tags and replacing the tags accordingly on save/load */
              or(
                find("<cite", local!item) <> 0,
                find("<Ignore-cite", local!item) <> 0
              ),
              if(
                /* reverting the ignored-cite tags to cite tags on save*/
                ri!revertIgnoredCitetags = true,
                regexreplaceall("<Ignore-cite", local!item, "<cite"),
                /* Ignoring the cite tags within the tables */
                regexreplaceall("<cite", local!item, "<Ignore-cite")
              ),
              if(
                /* Logic to remove the highlight tag */
                find("background-color:", local!item) <> 0,
                rule!AS_GCM_UT_removeHightlightTags(clauseText: local!item),
                local!item
              )
            ),
            local!item
          )
        )
      ),
      ""
    )
  )
)

if(
  any(
    a!isNotNullOrEmpty,
    { ri!clauseNumber, ri!uuid }
  ),
  a!localVariables(
    local!class: concat(
      "class=""cite ",
      joinarray(
        {
          if(
            /* Check whether the UUID is present else generate the UUID */
            a!isNullOrEmpty(ri!uuid),
            concat(
              stripwith(ri!clauseNumber," "),
              "_",
              rule!AS_GCM_BL_generateUuidForFillIns()
            ),
            ri!uuid
          ),
          ri!fillinType
        },
        " "
      ),
      """"
    ),
    if(
      ri!getOnlyClass,
      local!class,
      concat(
        "<cite ",
        local!class,
        ">",
        ri!value,
        "</cite>"
      )
    )
  ),
  ""
)

