  local!nestedFieldNames: difference(
    touniformstring(
      rule!AS_CO_UT_returnCdtFields(
        typeOf: ri!cdt,
        onlyPrimitiveFields: false()
      )
    ),
    touniformstring(
      rule!AS_CO_UT_returnCdtFields(
        typeOf: ri!cdt,
        onlyPrimitiveFields: true()
      )
    )
  ),
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!cdt,
    expression: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
      cdt: fv!item,
      fieldValuePairs: a!localVariables(
        local!cdt: fv!item,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!nestedFieldNames,
          expression: {
            field: fv!item,
            value: if(
              rule!AS_CO_UT_isBlank(
                rule!AS_CO_UT_indexPath(
                  cdt: local!cdt,
                  field: fv!item & "." & rule!AS_GCM_UT_getPrimaryKeyFieldByType(cdt: index(local!cdt, fv!item)),
                  indexDefault: null()
                )
              ),
              null,
              index(local!cdt, fv!item, null())
            )
          }
        )
      )
    )
  )
)

