      {
        index(
          local!unselectedTableFillinMaps,
          "tableText",
          null
        ),
        index(
          local!unselectedFillinMaps,
          "highlightTag",
          null
        )
      }
    ),
    replaceWith: rule!AS_CO_UT_rejectNullValuesFromArray(
      {
        index(
          local!unselectedTableFillinMaps,
          "tableTextWithoutFillIns",
          {}
        ),
        index(
          local!unselectedFillinMaps,
          "displayText",
          {}
        )
      }
