_a-0000ed9c-6a3c-8000-9e00-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!clauseSectionMappings,
  expression: rule!AS_GCM_setRecordFields(
    record: fv!item,
    alwaysOverwrite: {
      a!map(
        field: recordType!AS GCM R Clause Section Mapping.modifiedBy,
        value: a!defaultValue(
          value: ri!initiator,
          default: loggedInUser()
        )
      ),
      a!map(
        field: recordType!AS GCM R Clause Section Mapping.modifiedDatetime,
        value: now()
      )
    },
    neverOverwrite: {
      {
        a!map(
          field: recordType!AS GCM R Clause Section Mapping.createdBy,
          value: a!defaultValue(
            value: ri!initiator,
            default: loggedInUser()
          )
        ),
        a!map(
          field: recordType!AS GCM R Clause Section Mapping.createdDatetime,
          value: now()
        ),
        a!map(
          field: recordType!AS GCM R Clause Section Mapping.isActive,
          value: now()
        )
      }
    }
  )
)

