    ri!fillinMap.fillinType = cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_RICH_TEXT_TABLE,
    ri!fillinMap.fillinType = cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_TABLE
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: #"SYSTEM_SYSRULES_tagField"(
                  tags: #"SYSTEM_SYSRULES_tagItem_v1"(
                    text: rule!AS_GAM_CO_I18N_UT_displayLabel(
                      bundle: ri!bundle,
                      bundleKey: if(
                        length(
                          intersection(
                            ri!aiIdentifiedFillInsUuids,
                            if(
                              ri!fillinMap.fillInType = cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_TABLE,
                              ri!fillinMap.uuidsInTable,
                              ri!fillinMap.uuid
                            )
                          )
                        ) > 0,
                        "txt_AIIdentified",
                        "txt_ManuallyAdded"
                      )
                    ),
                    backgroundColor: rule!AS_GCM_getColorBasedOnColorName("PURPLE_1"),
                    textColor: rule!AS_GCM_getColorBasedOnColorName("PURPLE_4")
                  )
                )
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_GCM_CPS_fillInUntagTextAndIcon(
                  bundle: ri!bundle,
                  fillinMap: ri!fillinMap,
                  triggerSave: ri!triggerSave,
                  showFillinRec: ri!showFillinRec,
                  removeFillinsUuid: ri!removeFillinsUuid,
                  showRemoveConfirmation: ri!showRemoveConfirmation
                ),
                width: "MINIMIZE",
                showWhen: not(local!isTableFillIn)
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                width: "1X",
                showWhen: local!isTableFillIn,
                item: rule!AS_GCM_CPS_fillInUntagTextAndIcon(
                  bundle: ri!bundle,
                  fillinMap: ri!fillinMap,
                  triggerSave: ri!triggerSave,
                  showFillinRec: ri!showFillinRec,
                  removeFillinsUuid: ri!removeFillinsUuid,
                  showRemoveConfirmation: ri!showRemoveConfirmation
                )
              )
            },
            alignVertical: "MIDDLE",
            showWhen: local!isFillInTagSourceDisplayed
          ),
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                /* FILL IN TEXT */
                item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                  label: "Fill-in Text",
                  labelPosition: "COLLAPSED",
                  value: ri!fillinMap.displayText
                )
              )
            },
            marginBelow: "STANDARD",
            showWhen: and(
              not(local!isTableFillIn),
              local!isFillInTagSourceDisplayed
            )
          ),
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                /* FILL IN TEXT */
                item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                  label: "Fill-in Text",
                  labelPosition: "COLLAPSED",
                  value: ri!fillinMap.displayText
                )
              ),
              /* UN TAG FILL IN */
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_GCM_CPS_fillInUntagTextAndIcon(
                  bundle: ri!bundle,
                  fillinMap: ri!fillinMap,
                  triggerSave: ri!triggerSave,
                  showFillinRec: ri!showFillinRec,
                  removeFillinsUuid: ri!removeFillinsUuid,
                  showRemoveConfirmation: ri!showRemoveConfirmation
                ),
                width: "MINIMIZE",
                showWhen: not(local!isTableFillIn)
              )
            },
            marginBelow: "STANDARD",
            showWhen: and(
              not(local!isTableFillIn),
              not(local!isFillInTagSourceDisplayed)
            )
          ),
          #"SYSTEM_SYSRULES_sideBySideLayout"(
            items: {
              #"SYSTEM_SYSRULES_sideBySideItem"(
                item: rule!AS_GCM_DPL_CO_dropdownField(
                  label: "Fill-In Type",
                  labelPosition: "ABOVE",
                  required: true,
                  choiceLabels: if(
                    local!isTableFillIn,
                    ri!fillInTypeMap.displayText,
                    rule!AS_GCM_filterRecords(
                      filterType: "REJECT",
                      records: ri!fillInTypeMap,
                      field: "fillinType",
                      value: {
                        cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_TABLE,
                        cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_RICH_TEXT_TABLE
                      }
                    ).displayText
                  ),
                  choiceValues: if(
                    local!isTableFillIn,
                    ri!fillInTypeMap.fillinType,
                    rule!AS_GCM_filterRecords(
                      filterType: "REJECT",
                      records: ri!fillInTypeMap,
                      field: "fillinType",
                      value: {
                        cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_TABLE,
                        cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_RICH_TEXT_TABLE
                      }
                    ).fillinType
                  ),
                  placeholder: "",
                  value: ri!fillinMap.fillinType,
                  validations: {},
                  saveInto: {
                    a!save(ri!triggerSave, false),
                    a!save(
                      ri!clauseTextInRTE,
                      substitute(
                        ri!clauseTextInRTE,
                        joinarray(
                          {
                            ri!fillinMap.uuid,
                            ri!fillinMap.fillinType
                          },
                          " "
                        ),
                        joinarray({ ri!fillinMap.uuid, save!value }, " ")
                      )
                    ),
                    a!save(
                      ri!runTimeClauseText,
                      substitute(
                        ri!runTimeClauseText,
                        joinarray(
                          {
                            ri!fillinMap.uuid,
                            ri!fillinMap.fillinType
                          },
                          " "
                        ),
                        joinarray({ ri!fillinMap.uuid, save!value }, " ")
                      )
                    ),
                    a!save(
                      {
                        ri!fillinMap,
                        ri!fillInMapsList[wherecontains(
                          tostring(ri!fillinMap.uuid), touniformstring(ri!fillInMapsList.uuid)
                        )]
                      },
                      rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
                        cdt: ri!fillinMap,
                        fieldValuePairs: {
                          {
                            field: "citeTag",
                            value: substitute(
                              ri!fillinMap.citeTag,
                              joinarray(
                                {
                                  ri!fillinMap.uuid,
                                  ri!fillinMap.fillinType
                                },
                                " "
                              ),
                              joinarray({ ri!fillinMap.uuid, save!value }, " ")
                            )
                          },
                          {
                            field: "highlightTag",
                            value: substitute(
                              ri!fillinMap.highlightTag,
                              joinarray(
                                {
                                  ri!fillinMap.uuid,
                                  ri!fillinMap.fillinType
                                },
                                " "
                              ),
                              joinarray({ ri!fillinMap.uuid, save!value }, " ")
                            )
                          }
                        }
                      )
                    ),
                    a!save(
                      {
                        ri!fillinMap.fillinType,
                        ri!fillInMapsList[wherecontains(
                          tostring(ri!fillinMap.uuid), touniformstring(ri!fillInMapsList.uuid)
                        )].fillinType
                      },
                      save!value
                    )
                  },
                  accessibilityText: if(
                    ri!fillinMap.fillinType = cons!AS_GCM_TXT_CUSTOM_FILL_IN_TYPE_TABLE,
                    concat(
                      "Fill-in type for",
                      " ",
                      "Table Preview"
                    ),
                    "Fill-in type for" & rule!AS_GAM_UT_truncateText(
                      text: ri!fillinMap.displayText,
                      maxLength: cons!AS_GCM_INT_CHAR_LIMIT_FOR_FULL_FILLIN_INSTRUCTIONS
                    )
                  ),
                  searchDisplay: "AUTO",
                  marginAbove: "EVEN_LESS",
                  disabled: or(ri!showFillinRec, local!isTableFillIn)
                )
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"(
                width: "1X",
                showWhen: and(
                  local!isTableFillIn,
                  not(local!isFillInTagSourceDisplayed)
                ),
                item: rule!AS_GCM_CPS_fillInUntagTextAndIcon(
                  bundle: ri!bundle,
                  fillinMap: ri!fillinMap,
                  triggerSave: ri!triggerSave,
                  showFillinRec: ri!showFillinRec,
                  removeFillinsUuid: ri!removeFillinsUuid,
                  showRemoveConfirmation: ri!showRemoveConfirmation
                )
              )
            }
          )
        }
      )
    },
    alignVertical: "MIDDLE",
    spacing: "NONE"
  )
)

