a!localVariables(
  local!showEmptyState: rule!AS_CO_UT_isBlank(value: ri!fillinMaps),
  local!triggerSave: true,
  local!showBanner: true,
  local!removeFillinsUuid,
  local!isAddRecOrAutoPopulateReadOnlyMaps: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_forEach"(
      items: ri!fillinMaps,
      expression: a!map(uuid: fv!item.uuid, readOnly: true)
    ),
    refreshOnReferencedVarChange: false
  ),
  local!isDisableIdentifyFillInAction: or(
    rule!AS_CO_UT_isBlank(ri!clauseTextInRTE),
    ri!confirmationBannerShowWhen = true,
    rule!AS_CO_UT_isNotBlank(local!removeFillinsUuid),
    contains(
      a!defaultValue(
        value: toboolean(
          index(
            local!isAddRecOrAutoPopulateReadOnlyMaps,
            "readOnly",
            null
          )
        ),
        default: true
      ),
      false
    )
  ),
  local!toolTipMsgForDisabledAction: if(
    toboolean(local!isDisableIdentifyFillInAction) = true,
    rule!AS_GCM_toolTipMessageForIdentifyFillInsAction(
      clauseTextInRTE: ri!clauseTextInRTE,
      confirmationBannerShowWhen: ri!confirmationBannerShowWhen,
      removeFillinsUuid: local!removeFillinsUuid,
      isAddRecOrAutoPopulateReadOnly: index(
        local!isAddRecOrAutoPopulateReadOnlyMaps,
        "readOnly",
        null
      ),
      bundle: ri!bundle
    ),
    ""
  ),
  {
