                    local!thisDropdown_index,
                    null
                  ),
                  local!thisDropdown_availableSections: rule!AS_GCM_filterRecords(
                    records: ri!refSections,
                    field: recordType!AS GCM R Section.contractFormatId,
                    value: local!thisDropdown_contractFormat[recordType!AS GCM R Data.refDataId]
                  ),
                  local!thisDropdown_clauseSectionMappingIndex: rule!AS_GCM_filterRecords(
                    records: ri!refClause[recordType!AS GCM R Clause.additionalSections],
                    field: recordType!AS GCM R Clause Section Mapping.sectionId,
                    value: local!thisDropdown_availableSections[recordType!AS GCM R Section.sectionId],
                    filterType: "MATCH_INDICES"
                  ),
                  local!thisDropdown_clauseSectionMapping: rule!AS_CO_UT_cast(
                    type: recordType!AS GCM R Clause Section Mapping,
                    value: ri!refClause[recordType!AS GCM R Clause.additionalSections][local!thisDropdown_clauseSectionMappingIndex]
                  ),
                  if(
                    local!thisDropdown_Index > rule!AS_GAM_UT_ArrayLength(array: ri!refContractFormats),
                    {},
                    rule!AS_GCM_DPL_CO_dropdownField(
                      label: rule!AS_GAM_CO_I18N_UT_displayLabel(
                        bundle: ri!bundle,
                        bundleKey: local!thisDropdown_contractFormat[recordType!AS GCM R Data.refKey]
                      ),
                      labelPosition: "ABOVE",
                      choiceLabels: local!thisDropdown_availableSections[recordType!AS GCM R Section.sectionLabel],
                      choiceValues: local!thisDropdown_availableSections[recordType!AS GCM R Section.sectionId],
                      placeholder: "Select a Section",
                      value: a!match(
                        value: null,
                        whenTrue: local!thisDropdown_contractFormat[recordType!AS GCM R Data.refDataId] = cons!AS_GCM_REF_ID_CONTRACT_FORMAT_UCF,
                        then: ri!refClause[recordType!AS GCM R Clause.ucfSectionId],
                        whenTrue: rule!AS_CO_UT_booleanDefaultTrue(
                          boolean: local!thisDropdown_clauseSectionMapping[recordType!AS GCM R Clause Section Mapping.isActive]
                        ),
                        then: local!thisDropdown_clauseSectionMapping[recordType!AS GCM R Clause Section Mapping.sectionId],
                        default: null
                      ),
                      saveInto: a!match(
                        value: null,
                        whenTrue: local!thisDropdown_contractFormat[recordType!AS GCM R Data.refDataId] = cons!AS_GCM_REF_ID_CONTRACT_FORMAT_UCF,
                        then: a!save(
                          ri!refClause[recordType!AS GCM R Clause.ucfSectionId],
                          save!value
                        ),
                        /* New Section Selection */
                        whenTrue: rule!AS_CO_UT_isBlank(
                          local!thisDropdown_clauseSectionMappingIndex
                        ),
                        then: a!save(
                          ri!refClause[recordType!AS GCM R Clause.additionalSections],
                          append(
                            ri!refClause[recordType!AS GCM R Clause.additionalSections],
                            recordType!AS GCM R Clause Section Mapping(
                              recordType!AS GCM R Clause Section Mapping.sectionId: save!value
                            )
                          )
                        ),
                        /* Updating selected section */
                        default: a!save(
                          ri!refClause[recordType!AS GCM R Clause.additionalSections],
                          a!match(
                            value: save!value,
                            whenTrue: rule!AS_CO_UT_isNotBlank(fv!value),
                            then: #"urn:appian:function:v1:a:update"(
                              ri!refClause[recordType!AS GCM R Clause.additionalSections],
                              local!thisDropdown_clauseSectionMappingIndex,
                              #"urn:appian:function:v1:a:update"(
                                local!thisDropdown_clauseSectionMapping,
                                {
                                  recordType!AS GCM R Clause Section Mapping.sectionId,
                                  recordType!AS GCM R Clause Section Mapping.isActive
                                },
                                { fv!value, true }
                              )
                            ),
                            whenTrue: rule!AS_CO_UT_isBlank(
                              local!thisDropdown_clauseSectionMapping[recordType!AS GCM R Clause Section Mapping.mappingId]
                            ),
                            then: remove(
                              ri!refClause[recordType!AS GCM R Clause.additionalSections],
                              local!thisDropdown_clauseSectionMappingIndex
                            ),
                            default: #"urn:appian:function:v1:a:update"(
                              ri!refClause[recordType!AS GCM R Clause.additionalSections],
                              local!thisDropdown_clauseSectionMappingIndex,
                              #"urn:appian:function:v1:a:update"(
                                local!thisDropdown_clauseSectionMapping,
                                recordType!AS GCM R Clause Section Mapping.isActive,
                                false
                              )
                            )
                          )
                        )
                      ),
                      searchDisplay: "AUTO",
                      required: local!thisDropdown_contractFormat[recordType!AS GCM R Data.refDataId] = cons!AS_GCM_REF_ID_CONTRACT_FORMAT_UCF
                    )
                  )
                )
              )
            }
          )
        )
      )
    )
  }
)

