if(
  rule!AS_CO_UT_isBlank(
    ri!refClause[recordType!AS GCM R Clause.clauseNumber]
  ),
  null,
  a!localVariables(
    /*Check if the Clause Html is returned successfully*/
    local!clauseHtml: rule!AS_GCM_INT_getAcquisitionDocumentHtml(
      uniqueNumber: ri!refClause[recordType!AS GCM R Clause.clauseNumber],
      sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
    ),
    if(
      index(local!clauseHtml, "success", null) <> "true",
      /*--> if clause Html is not returned, check if clause number/prescription number is invalid and update helper cdt accordingly*/
      a!localVariables(
        local!invalidClauseNumber: rule!AS_CO_UT_isBlank(
          value: rule!AS_GCM_BL_doesClauseHtmlDocExist(
            clauses: ri!refClause,
            sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
          )
        ),
        local!prescriptionHtml: if(
          a!isNullOrEmpty(
            ri!refClause[recordType!AS GCM R Clause.prescriptionNumber]
          ),
          "",
          rule!AS_GCM_INT_getAcquisitionDocumentHtml(
            uniqueNumber: ri!refClause[recordType!AS GCM R Clause.prescriptionNumber],
            sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
          )
        ),
        local!invalidPrescriptionNumber: if(
          index(local!prescriptionHtml, "success", null) <> "true",
          rule!AS_CO_UT_isBlank(
            value: rule!AS_GCM_BL_doesClausePrescriptionHtmlDocExist(
              clauses: ri!refClause,
              sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
            )
          ),
          false
        ),
        local!prescriptionText: if(
          index(local!prescriptionHtml, "success", null) = "true",
          rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
            responseBody: local!prescriptionHtml.result.body,
            isClauseText: false()
          ),
          ri!refClauseText.prescriptionText
        ),
        a!map(
          refClause: rule!AS_GCM_setRecordFields(
            record: ri!refClause,
            childRelationships: a!map(
              field: recordType!AS GCM R Clause.acquisitionUpdateHelper,
              value: recordType!AS GCM P Acquisition Update Helper(
                recordType!AS GCM P Acquisition Update Helper.refClauseId: ri!refClause[recordType!AS GCM R Clause.clauseId],
                recordType!AS GCM P Acquisition Update Helper.refClause.refSourceId: ri!refClause[recordType!AS GCM R Clause.refSourceId],
                recordType!AS GCM P Acquisition Update Helper.refClauseTextId: ri!refClauseText.clauseTextId,
                recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber: local!invalidClauseNumber,
                recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber: local!invalidPrescriptionNumber,
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextError: if(
                  local!invalidPrescriptionNumber,
                  null,
                  index(local!prescriptionHtml, "error", null)
                ),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess: if(
                  local!invalidPrescriptionNumber,
                  null,
                  index(local!prescriptionHtml, "success", null)
                ),
                recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned: if(
                  local!invalidPrescriptionNumber = true,
                  false,
                  rule!AS_CO_UT_isBlank(value: local!prescriptionText)
                ),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!prescriptionText),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClauseText.prescriptionText,
                      value2: local!prescriptionText
                    )
                  )
                )
              )
            )
          ),
          refClauseText: ri!refClauseText
        )
      ),
      /*--> if clause Html is returned, update refClause, refClauseText and helper cdt accordingly*/
      a!localVariables(
        /*Fetch Clause text*/
        local!clauseText: rule!AS_GCM_UT_formatMergedCells(
          rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
            responseBody: local!clauseHtml.result.body,
            isClauseText: true()
          )
        ),
        local!invalidClauseTitle: if(
          /*Check If existing Alternate value is valid*/
          or(
            contains(
              rule!AS_GCM_DFARS_getAlternateValuesFromSync(clauseHtmlBody: local!clauseText),
              ri!refClause[recordType!AS GCM R Clause.alternateValue]
            ),
            a!isNullOrEmpty(
              ri!refClause[recordType!AS GCM R Clause.alternateValue]
            )
          ),
          false,
          true
        ),
        local!prescriptionNumber: rule!AS_GCM_DFARS_getPrescriptionNumberFromSync(clauseHtml: local!clauseText),
        local!prescriptionHtml: if(
          a!isNullOrEmpty(local!prescriptionNumber),
          "",
          rule!AS_GCM_INT_getAcquisitionDocumentHtml(
            uniqueNumber: local!prescriptionNumber,
            sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
          )
        ),
        /*Check if invalid Prescription number*/
        local!invalidPrescriptionNumber: if(
          and(
            index(local!prescriptionHtml, "success", null) <> "true",
            a!isNotNullOrEmpty(local!prescriptionNumber)
          ),
          rule!AS_CO_UT_isBlank(
            value: rule!AS_GCM_BL_doesClausePrescriptionHtmlDocExist(
              clauses: recordType!AS GCM R Clause(
                recordType!AS GCM R Clause.prescriptionNumber: local!prescriptionNumber
              ),
              sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
            )
          ),
          false
        ),
        /*Fetch Prescription Text*/
        local!prescriptionText: if(
          index(local!prescriptionHtml, "success", null) = "true",
          rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
            responseBody: local!prescriptionHtml.result.body,
            isClauseText: false()
          ),
          ri!refClauseText.prescriptionText
        ),
        local!effectiveDate: rule!AS_GCM_DFARS_updateEffectiveDateFromSync(
          clauseHtml: local!clauseHtml,
          alternateValue: ri!refClause[recordType!AS GCM R Clause.alternateValue]
        ),
        local!fillIn: rule!AS_GCM_updateFillInFromSync(clauseHtml: local!clauseHtml),
        local!editable: rule!AS_GCM_DFARS_updateEditableFromSync(clauseHtml: local!clauseHtml),
        local!clauseTitle: trim(
          substitute(
            striphtml(
              regexfirstmatch(
                "<title(.*?)</title>",
                local!clauseHtml.result.body
              )
            ),
            ri!refClause[recordType!AS GCM R Clause.clauseNumber],
            ""
          )
        ),
        local!refType: rule!AS_GCM_DFARS_getRefTypeFromSync(clauseText: local!clauseText),
        local!refClause: if(
          local!invalidClauseTitle = true,
          ri!refClause,
          rule!AS_GCM_setRecordFields(
            record: ri!refClause,
            alwaysOverwrite: {
              a!map(
                field: recordType!AS GCM R Clause.fillIn,
                value: a!match(
                  value: local!fillIn,
                  whenTrue: rule!AS_CO_UT_isBlank(fv!value),
                  then: ri!refClause[recordType!AS GCM R Clause.fillIn],
                  whenTrue: toboolean(fv!value) = true,
                  then: true,
                  default: null
                )
              ),
              a!map(
                field: recordType!AS GCM R Clause.clauseTitle,
                value: a!defaultValue(
                  value: local!clauseTitle,
                  default: ri!refClause[recordType!AS GCM R Clause.clauseTitle]
                )
              ),
              a!map(
                field: recordType!AS GCM R Clause.refTypeId,
                value: a!defaultValue(
                  value: local!refType,
                  default: ri!refClause[recordType!AS GCM R Clause.refTypeId]
                )
              ),
              a!map(
                field: recordType!AS GCM R Clause.effectiveDate,
                value: a!defaultValue(
                  value: local!effectiveDate,
                  default: ri!refClause[recordType!AS GCM R Clause.effectiveDate]
                )
              ),
              a!map(
                field: recordType!AS GCM R Clause.prescriptionNumber,
                value: a!defaultValue(
                  value: local!prescriptionNumber,
                  default: ri!refClause[recordType!AS GCM R Clause.prescriptionNumber]
                )
              ),
              a!map(
                field: recordType!AS GCM R Clause.editable,
                value: a!defaultValue(
                  value: local!editable,
                  default: ri!refClause[recordType!AS GCM R Clause.editable]
                )
              )
            }
          )
        ),
        a!map(
          /*Updating the Helper CDT*/
          refClause: rule!AS_GCM_setRecordFields(
            record: local!refClause,
            childRelationships: a!map(
              field: recordType!AS GCM R Clause.acquisitionUpdateHelper,
              value: recordType!AS GCM P Acquisition Update Helper(
                recordType!AS GCM P Acquisition Update Helper.refClauseId: ri!refClause[recordType!AS GCM R Clause.clauseId],
                recordType!AS GCM P Acquisition Update Helper.refClauseTextId: ri!refClauseText.clauseTextId,
                recordType!AS GCM P Acquisition Update Helper.refClause.refSourceId: ri!refClause[recordType!AS GCM R Clause.refSourceId],
                recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated: and(
                  rule!AS_CO_UT_isNotBlank(local!refClause),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClause,
                      value2: local!refClause
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber: false,
                recordType!AS GCM P Acquisition Update Helper.invalidClauseTitle: local!invalidClauseTitle,
                recordType!AS GCM P Acquisition Update Helper.clauseTextError: index(local!clauseHtml, "error", null),
                recordType!AS GCM P Acquisition Update Helper.clauseTextSuccess: index(local!clauseHtml, "success", null) = "true",
                recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned: if(
                  local!invalidClauseTitle = true,
                  false,
                  rule!AS_CO_UT_isBlank(value: local!clauseText)
                ),
                recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!clauseText),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClauseText.clauseText,
                      value2: local!clauseText
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber: local!invalidPrescriptionNumber,
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextError: if(
                  local!invalidPrescriptionNumber,
                  null,
                  index(local!prescriptionHtml, "error", null)
                ),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess: if(
                  local!invalidPrescriptionNumber,
                  null,
                  index(local!prescriptionHtml, "success", null)
                ),
                recordType!AS GCM P Acquisition Update Helper.noEffectiveDateReturned: if(
                  local!invalidClauseTitle = true,
                  false,
                  rule!AS_CO_UT_isBlank(value: local!effectiveDate)
                ),
                recordType!AS GCM P Acquisition Update Helper.effectiveDateUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!effectiveDate),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClause[recordType!AS GCM R Clause.effectiveDate],
                      value2: local!effectiveDate
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.noFillInReturned: if(
                  local!invalidClauseTitle = true,
                  false,
                  rule!AS_CO_UT_isBlank(value: local!fillIn)
                ),
                recordType!AS GCM P Acquisition Update Helper.fillInUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!fillIn),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClause[recordType!AS GCM R Clause.fillIn],
                      value2: local!fillIn
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned: if(
                  or(
                    local!invalidPrescriptionNumber = true,
                    a!isNullOrEmpty(local!prescriptionNumber)
                  ),
                  false,
                  rule!AS_CO_UT_isBlank(value: local!prescriptionText)
                ),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!prescriptionText),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClauseText.prescriptionText,
                      value2: local!prescriptionText
                    )
                  )
                )
              )
            )
          ),
          refClauseText: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
            cdt: ri!refClauseText,
            fieldValuePairs: {
              {
                field: "clauseText",
                value: if(
                  and(
                    local!invalidClauseTitle = true,
                    rule!AS_CO_UT_isBlank(local!clauseText)
                  ),
                  ri!refClauseText.clauseText,
                  local!clauseText
                )
              },
              {
                field: "prescriptionText",
                value: if(
                  and(
                    or(
                      local!invalidPrescriptionNumber = true,
                      local!invalidClauseTitle = true
                    ),
                    rule!AS_CO_UT_isBlank(local!prescriptionText)
                  ),
                  ri!refClauseText.prescriptionText,
                  local!prescriptionText
                )
              }
            }
          )
        )
      )
    )
  )
)

