_a-0000ec46-fcf4-8000-9d91-011c48011c48_.sail_code:
/*This rule is used perform below manipulations to the clause text during sync process

#1 Adding span hidden tags for H1 tags and 'As prescribed' paragraph as we don't 
need to show this in document generation since we are already adding a clause header.

#2 Rectifying the misplaced checkbox cite tags where the checkbox is not enclosed within the cite tag */
if(
  a!isNullOrEmpty(ri!clauseText),
  "",
  a!localVariables(
    local!htmlToHide: regexfirstmatch(
      "<h1.*?As prescribed.*?\d{4}\).*?</p>",
      ri!clauseText
    ),
    /* Adding the hidden tag only for clause text */
    local!clauseText: if(
      and(
        ri!isClauseText,
        /* As some of the DFARS clause text are unstructured */
        a!isNotNullOrEmpty(local!htmlToHide)
      ),
      substitute(
        ri!clauseText,
        local!htmlToHide,
        "<span hidden="""">" & local!htmlToHide & "</span>"
      ),
      ri!clauseText
    ),
    /*Scenario: cite tag for the checkbox is misplaced. It is tagged to the text instead of checkbox. Ex: 52.212-3*/
    /*Retrieving all mistagged checkboxes*/
    local!mistaggedCheckboxes: rule!AS_GCM_UT_regexsearch(
      /* Test this Regex in https://regex101.com/r/LE0egm/1 */
      pattern: "(?:<span><\/span>|)(.*?)(<cite.*?" & cons!AS_GCM_TXT_FILL_IN_TYPE_CHECKBOX & """>)([^_]+)<\/cite>",
      searchString: local!clauseText,
      regexFlag: "gm",
      returnGroups: true
    ),
    /*Correcting the mistagged checkboxes*/
    local!retaggedCheckboxes: #"SYSTEM_SYSRULES_forEach"(
      items: local!mistaggedCheckboxes,
      expression: fv!item.groups[2].match & char(9633) & "</cite>" & fv!item.groups[1].match & fv!item.groups[3].match,

    ),
    if(
      rule!AS_CO_UT_isBlank(local!mistaggedCheckboxes),
      local!clauseText,
      rule!AS_GCM_substituteListFromTextUsingReduce(
        text: local!clauseText,
        find: local!mistaggedCheckboxes.match,
        replaceWith: local!retaggedCheckboxes
      )
    )
  )
)

_a-0000e643-f7f1-8000-9bca-011c48011c48_.sail_code:
/*This rule don't have test case since it involves passing integrationResonseMap as a rule input or a zipDocument
The testing will be handled by automated integation testing in AT environment.
To test this rule pass in a integrationResponseMap
*/
a!localVariables(
  local!staticData: rule!AS_GCM_DFARS_getRefTypeFromSync(source: cons!AS_GCM_REF_ID_SOURCE_FAR),
  local!fileContents: a!defaultValue(
    value: ri!integrationResponseMaps,
    default: rule!AS_GCM_getFileDataViaZipDocumentOrGitHub(
      zipDocument: ri!zipDocument,
      fileNames: local!staticData.requiredFiles,
      sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
    )
  ),
  local!farMatrix: rule!AS_CO_UT_filterCdtByField(
    cdt: local!fileContents,
    field: "fileName",
    value: local!staticData.requiredFiles[1]
  )[1],
  local!farDitamap: rule!AS_CO_UT_filterCdtByField(
    cdt: local!fileContents,
    field: "fileName",
    value: local!staticData.requiredFiles[2]
  )[1],
  local!farMatrixDocOrXml: local!farMatrix.result.body,
  /*This will be used for getting whether the clauses are editable or not.*/
  local!editable: rule!AS_GCM_UT_extractEditableFromFarDitaMap(
    farDitamapDocOrXml: local!farDitamap.result.body
  ),
  local!headers: rule!AS_GCM_BL_getHeaderFromFarMatrixData(
    farMatrixDocOrXml: local!farMatrixDocOrXml
  ),
  /*  Getting indices of the columns for only AS_GCM_TXT_ACQUISITION_REQUIRED_CLAUSES_COLUMN_HEADERS_FOR_SYNC  */
  local!validColumnIndices: wherecontains(
    cons!AS_GCM_TXT_ACQUISITION_REQUIRED_CLAUSES_COLUMN_HEADERS_FOR_SYNC,
    local!headers
  ),
  local!provisionOrClauseColumnHeader: rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "provisionOrClause"),
  local!rows: rule!AS_GCM_BL_getRowsFromFarMatrixData(
    clauseNumber: ri!clauseNumber,
    farMatrixDocOrXml: local!farMatrixDocOrXml,
    clauseNumbColumnIndex: wherecontains(
      local!provisionOrClauseColumnHeader,
      local!headers
    )
  ),
  local!data: #"SYSTEM_SYSRULES_forEach"(
    items: local!rows,
    expression: a!localVariables(
      local!clauseData: xpathsnippet(fv!item, "//row/entry/p"),
      local!clauseTitle: tostring(
        reject(
          a!isNullOrEmpty,
          xpathsnippet(
            local!clauseData[wherecontains(
              local!provisionOrClauseColumnHeader, local!headers
            )],
            "//p/text()"
          )
        )
      ),
      local!clauseNumber: substitute(
        tostring(
          xpathsnippet(
            local!clauseData[wherecontains(
              local!provisionOrClauseColumnHeader, local!headers
            )],
            "//p/xref/text()"
          )
        ),
        "_" & cons!AS_GCM_CLAUSE_TITLE_ALTERNATE,
        ""
      ),
      if(
        and(
          local!clauseTitle <> ri!clauseTitle,
          rule!AS_CO_UT_isNotBlank(ri!clauseTitle)
        ),
        {},
        a!flatten(
          {
            #"SYSTEM_SYSRULES_forEach"(
              items: merge(
                index(
                  local!headers,
                  local!validColumnIndices,
                  {}
                ),
                index(
                  local!clauseData,
                  local!validColumnIndices,
                  {}
                )
              ),
              expression: a!localVariables(
                local!data: tostring(
                  xpathsnippet(
                    fv!item[2],
                    if(
                      contains(
                        rule!AS_GCM_getFarMatrixColumnHeadersByKeys(
                          keys: { "provisionOrClause", "prescribedIn" }
                        ),
                        fv!item[1]
                      ),
                      "//p/xref/text()",
                      "//p/text()"
                    )
                  )
                ),
                a!map(
                  column: fv!item[1],
                  value: trim(
                    if(
                      fv!item[1] = local!provisionOrClauseColumnHeader,
                      local!clauseNumber,
                      local!data
                    )
                  )
                )
              )
            ),
            a!map(
              column: rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "clauseTitle"),
              value: trim(
                substitute(local!clauseTitle, char(10), char(32))
              )
            ),
            a!map(
              column: rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "editable"),
              value: rule!AS_GCM_UT_filterEditableValueByClauseTitle(
                clauseNumber: local!clauseNumber,
                clauseName: local!clauseTitle,
                editable: local!editable
              )
            ),
            a!map(
              column: rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "alternateValue"),
              value: rule!AS_GCM_UT_fetchAlternateValueFromClauseTitle(clauseTitle: local!clauseTitle)
            )
          }
        ),

      )
    )
  ),
  /* filtering all the clause number */
  local!clauseNumber: rule!AS_CO_UT_filterCdtByField(
    cdt: local!data,
    field: "column",
    value: local!provisionOrClauseColumnHeader
  ),
  {
    data: #"SYSTEM_SYSRULES_forEach"(
      items: local!data,
      expression: a!localVariables(
        a!localVariables(
          /* find the clauses which has same clause number */
          local!getSameClauses: index(
            local!data,
            wherecontains(
              fv!item[1].value,
              touniformstring(local!clauseNumber.value)
            ),
            {}
          ),
          local!baseClauseTitle: rule!AS_CO_UT_filterCdtByField(
            cdt: local!getSameClauses[1],
            field: "column",
            value: rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "clauseTitle")
          ).value[1],
          /* Update the base clause title to the alternate clauses */
          rule!AS_CO_UT_updateCdtFields(
            cdt: local!data[fv!index],
            fields: "value",
            indices: wherecontains(
              rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "clauseTitle"),
              fv!item.column
            ),
            newValues: local!baseClauseTitle
          )
        )
      )
    ),
    /* For Manual Syncs need to consider only local!farMatrix and for weekly sync
      we can consider local!farDitamap since this rule will not be invoked if local!farMatrix fails */
    success: if(
      a!isNotNullOrEmpty(ri!clauseNumber),
      local!farMatrix.success,
      local!farDitamap.success
    ),
    error: if(
      a!isNotNullOrEmpty(ri!clauseNumber),
      local!farMatrix.error,
      local!farDitamap.error
    )
  }
)

