    rule!AS_CO_UT_compareStrong(
      value1: ri!originalObject,
      value2: ri!newObject
    ),
    ri!externalAction <> cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_INITIATED
  ),
  {},
  a!localVariables(
    local!auditConfig: rule!AS_CO_UT_dynamicDictionary(
      fieldValuePairs: {
        {
          field: ri!auditConfig.idField,
          value: rule!AS_CO_UT_replaceNull(
            nullableValue: index(
              ri!originalObject,
              ri!auditConfig.idField,
              null
            ),
            replacementValue: index(
              ri!newObject,
              ri!auditConfig.idField,
              null
            )
          )
        },
        if(
          rule!AS_CO_UT_isNotBlank(ri!auditConfig.foreignIdField),
          {
            field: if(
              length(
                split(ri!auditConfig.foreignIdField, ".")
              ) = 2,
              split(ri!auditConfig.foreignIdField, ".")[2],
              split(ri!auditConfig.foreignIdField, ".")[1]
            ),
            value: rule!AS_CO_UT_replaceNull(
              nullableValue: rule!AS_CO_UT_indexPath(
                cdt: ri!originalObject,
                field: ri!auditConfig.foreignIdField
              ),
              replacementValue: rule!AS_CO_UT_indexPath(
                cdt: ri!newObject,
                field: ri!auditConfig.foreignIdField
              )
            )
          },
          {}
        ),
        { field: "username", value: ri!username },
        { field: "timestamp", value: now() },
        { field: "auditType", value: ri!auditType },
        {
          field: "auditActionCode",
          value: rule!AS_ADT_UT_determineAuditActionCode(
            originalObject: ri!originalObject,
            newObject: ri!newObject,
            auditConfig: ri!auditConfig,
            externalAction: ri!externalAction
          )
        },
        {
          field: "simpleFieldChanges",
          value: rule!AS_ADT_UT_buildSimpleFieldChanges(
            simpleFields: ri!auditConfig.simpleFields,
            originalObject: ri!originalObject,
            newObject: ri!newObject
          )
        },
        if(
          rule!AS_CO_UT_isBlank(ri!auditConfig.complexFields),
          {},
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!auditConfig.complexFields,
            expression: {
              field: fv!item.auditFieldName,
              value: rule!AS_ADT_UT_calculateAudit_MultipleObjects(
                originalObjects: index(ri!originalObject, fv!item.fieldName, {}),
                newObjects: index(ri!newObject, fv!item.fieldName, {}),
                auditConfig: fv!item,
                username: ri!username,
                externalAction: ri!externalAction
              )
            }
          )
        )
      }
    ),
    if(
      /*To make the action code "UNCHANGED" if the object is unmodified(excluding the metadata)*/
      and(
        rule!AS_CO_UT_not(
          contains(
            a!flatten(
              #"SYSTEM_SYSRULES_forEach"(
                items: {
                  "simpleFieldChanges",
                  index(
                    ri!auditConfig.complexFields,
                    "auditFieldName",
                    null
                  )
                },
                expression: or(
                  rule!AS_CO_UT_isBlank(index(local!auditConfig, fv!item, null)),
                  if(
                    contains(
                      touniformstring(
                        index(
                          ri!auditConfig.complexFields,
                          "auditFieldName",
                          null
                        )
                      ),
                      tostring(fv!item)
                    ),
                    not(
                      contains(
                        #"SYSTEM_SYSRULES_forEach"(
                          items: rule!AS_CO_UT_indexPath(cdt: local!auditConfig, field: fv!item),
                          expression: tostring(fv!item.auditActionCode) = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
                        ),
                        false
                      )
                    ),
                    false
                  )
                )
              )
            ),
            false
          )
        ),
        or(
          tostring(
            index(
              local!auditConfig,
              "auditActionCode",
              null
            )
          ) = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MODIFY,
          ri!externalAction = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MANUAL_UPDATE,
          ri!externalAction = cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_MANUAL_SYNC
        )
      ),
      rule!AS_CO_UT_updateCdtFields(
        cdt: local!auditConfig,
        fields: "auditActionCode",
        newValues: cons!AS_ADT_ENUM_AUDIT_ACTION_CODE_UNCHANGED
      ),
      local!auditConfig
    )
  )
)

