    rule!AS_GAM_ADT_UT_buildFieldLevelAuditEntityData(auditParams: ri!auditParams)
  )
)

_a-0000e643-f7f1-8000-9bca-011c48011c48_.sail_code:
a!localVariables(
  local!acquisitionUpdateHelper: ri!refClause[recordType!AS GCM R Clause.acquisitionUpdateHelper],
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null),
  local!allSuccess: and(
    if(
      tointeger(
        ri!refClause[recordType!AS GCM R Clause.refSourceId]
      ) = cons!AS_GCM_REF_ID_SOURCE_DFARS,
      {},
      {
        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.farMatrixSuccess],
        toboolean(
          local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noDataReturned]
        ) = false
      }
    ),
    local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextSuccess],
    local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess],
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber]
    ) = false,
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber]
    ) = false,
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned]
    ) = false,
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned]
    ) = false,
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseTitle]
    ) = false
  ),
  local!isFailure: or(
    if(
      tointeger(
        ri!refClause[recordType!AS GCM R Clause.refSourceId]
      ) = cons!AS_GCM_REF_ID_SOURCE_DFARS,
      {},
      toboolean(
        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.farMatrixSuccess]
      ) = false
    ),
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextSuccess]
    ) = false,
    toboolean(
      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess]
    ) = false
  ),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    contents: {
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        labelPosition: "COLLAPSED",
        value: {
          char(10),
          char(10),
          char(10),
          char(10),
          char(10),
          char(10),
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: if(
              local!allSuccess,
              "check-circle",
              if(
                local!isFailure,
                "times-circle",
                "exclamation-circle"
              )
            ),
            color: if(
              local!allSuccess,
              if(
                or(
                  toboolean(
                    local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated]
                  ) = false,
                  toboolean(
                    local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated]
                  ) = false,
                  toboolean(
                    local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated]
                  ) = false
                ),
                "ACCENT",
                "POSITIVE"
              ),
              if(local!isFailure, "NEGATIVE", "#F1C40F")
            ),
            size: "EXTRA_LARGE"
          ),
          char(10),
          char(10),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: if(
                  local!allSuccess,
                  /*All three integrations are success*/
                  if(
                    and(
                      toboolean(
                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated]
                      ) = false,
                      toboolean(
                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated]
                      ) = false,
                      toboolean(
                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated]
                      ) = false,
                      toboolean(
                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.effectiveDateUpdated]
                      ) = false,
                      toboolean(
                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.fillInUpdated]
                      ) = false
                    ),
                    /*Sync was successful, but clause details were up to date and no update was made*/
                    "",
                    /*Sync was successful, and the following were updated:*/
                    {
                      "",
                      char(10),
                      #"SYSTEM_SYSRULES_forEach"(
                        items: rule!AS_CO_UT_rejectNullValuesFromArray(
                          {
                            if(
                              or(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated],
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.effectiveDateUpdated],
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.fillInUpdated]
                              ),
                              "",
                              null
                            ),
                            if(
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated],
                              "",
                              ""
                            ),
                            if(
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated],
                              "",
                              ""
                            )
                          }
                        ),
                        expression: { fv!index & "." & fv!item & char(10) }
                      )
                    }
                  ),
                  if(
                    local!isFailure,
                    /*Unable to connect to Acquisition.gov at this time. Please try again later.*/
                    "",
                    /*Partially Success- One or more data not returned*/
                    if(
                      or(
                        toboolean(
                          local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noDataReturned]
                        ) = true,
                        toboolean(
                          local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned]
                        ) = true,
                        toboolean(
                          local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned]
                        ) = true,
                        toboolean(
                          local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noEffectiveDateReturned]
                        ) = true,
                        toboolean(
                          local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noFillInReturned]
                        ) = true
                      ),
                      {
                        rule!AS_GAM_CO_I18N_UT_displayLabel(
                          bundle: local!bundle,
                          bundleKey: "txt_SyncedNoDataReturned",
                          arguments: #"SYSTEM_SYSRULES_forEach"(
                            items: rule!AS_CO_UT_rejectNullValuesFromArray(
                              {
                                if(
                                  or(
                                    toboolean(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noDataReturned]
                                    ) = true,
                                    toboolean(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noEffectiveDateReturned]
                                    ) = true,
                                    toboolean(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noFillInReturned]
                                    ) = true
                                  ),
                                  "",
                                  null
                                ),
                                if(
                                  local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned],
                                  "",
                                  ""
                                ),
                                if(
                                  local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned],
                                  "",
                                  ""
                                )
                              }
                            ),
                            expression: {
                              if(
                                fv!isLast,
                                if(
                                  fv!index = 1,
                                  fv!item,
                                  "" & " " & fv!item
                                ),
                                fv!item & ", "
                              )
                            }
                          )
                        ),
                        {
                          if(
                            or(
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated],
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated],
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated],
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.effectiveDateUpdated],
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.fillInUpdated]
                            ),
                            {
                              char(10),
                              "",
                              char(10),
                              #"SYSTEM_SYSRULES_forEach"(
                                items: rule!AS_CO_UT_rejectNullValuesFromArray(
                                  {
                                    if(
                                      or(
                                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated],
                                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.effectiveDateUpdated],
                                        local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.fillInUpdated]
                                      ),
                                      "",
                                      null
                                    ),
                                    if(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated],
                                      "",
                                      ""
                                    ),
                                    if(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated],
                                      "",
                                      ""
                                    )
                                  }
                                ),
                                expression: { fv!index & "." & fv!item & char(10) }
                              )
                            },
                            ""
                          )
                        }
                      },
                      /*Sync unsuccessful or partially successful - Clause / presc number invalid*/
                      if(
                        or(
                          toboolean(
                            local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber]
                          ) = true,
                          toboolean(
                            local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseTitle]
                          ) = true
                        ),
                        {
                          rule!AS_GAM_CO_I18N_UT_displayLabel(
                            bundle: local!bundle,
                            bundleKey: "txt_SyncUnsuccessful",
                            arguments: if(
                              toboolean(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber]
                              ) = true,
                              "",
                              ""
                            ) & if(
                              toboolean(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber]
                              ) = true,
                              " " & "" & " " & "",
                              ""
                            )
                          ),
                          if(
                            or(
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated],
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated]
                            ),
                            {
                              char(10),
                              "",
                              char(10),
                              #"SYSTEM_SYSRULES_forEach"(
                                items: rule!AS_CO_UT_rejectNullValuesFromArray(
                                  {
                                    if(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated],
                                      "",
                                      null
                                    ),
                                    if(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated],
                                      "",
                                      ""
                                    ),
                                    if(
                                      local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated],
                                      "",
                                      ""
                                    )
                                  }
                                ),
                                expression: { fv!index & "." & fv!item & char(10) }
                              )
                            },
                            ""
                          )
                        },
                        if(
                          and(
                            or(
                              toboolean(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber]
                              ) = true,
                              toboolean(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseTitle]
                              ) = true
                            ),
                            toboolean(
                              local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber]
                            ) <> true
                          ),
                          {
                            if(
                              toboolean(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated]
                              ) = true,
                              ". " & rule!AS_GAM_CO_I18N_UT_displayLabel(
                                bundle: local!bundle,
                                bundleKey: "txt_DataUpdated",
                                arguments: ""
                              ),
                              ". " & rule!AS_GAM_CO_I18N_UT_displayLabel(
                                bundle: local!bundle,
                                bundleKey: "txt_UpToDate",
                                arguments: ""
                              )
                            )
                          },
                          if(
                            and(
                              toboolean(
                                local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber]
                              ) = true,
                              or(
                                toboolean(
                                  local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber]
                                ) <> true,
                                toboolean(
                                  local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.invalidClauseTitle]
                                ) <> true
                              )
                            ),
                            {
                              rule!AS_GAM_CO_I18N_UT_displayLabel(
                                bundle: local!bundle,
                                bundleKey: "txt_SyncPartiallySuccessful",
                                arguments: ""
                              ),
                              if(
                                toboolean(
                                  local!acquisitionUpdateHelper[recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated]
                                ) = true,
                                ". " & rule!AS_GAM_CO_I18N_UT_displayLabel(
                                  bundle: local!bundle,
                                  bundleKey: "txt_DataUpdated",
                                  arguments: ""
                                ),
                                null
                              )
                            },
                            ""
                          )
                        )
                      )
                    )
                  )
                )
              )
            },
            color: "STANDARD",
            size: "MEDIUM_PLUS"
          )
        },
        align: "CENTER"
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
      buttons: {
        #"SYSTEM_SYSRULES_ButtonWidget"(
          label: "Close",
          style: "PRIMARY",
          submit: true,
          value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
          saveInto: {},
          accessibilityText: ""
        )
      },
      align: "END"
    )
