  typeof(ri!cdt),
  if(
    rule!AS_CO_UT_isBlank(ri!cdt),
    ri!cdt,
    remove(
      a!flatten(ri!cdt),
      rule!AS_CO_UT_returnCdtIndicesByField(
        cdt: ri!cdt,
        field: ri!field,
        value: ri!value
      )
    )
  )
)

_a-0000e643-f7f1-8000-9bca-011c48011c48_.sail_code:
if(
  rule!AS_CO_UT_isBlank(
    ri!refClause[recordType!AS GCM R Clause.clauseNumber]
  ),
  null,
  a!localVariables(
    /*Check if the Clause Number is invalid*/
    local!invalidClauseNumber: rule!AS_CO_UT_isBlank(
      value: rule!AS_GCM_BL_doesClauseHtmlDocExist(
        clauses: ri!refClause,
        sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
      )
    ),
    if(
      local!invalidClauseNumber = true,
      /*--> if clause number invalid, check if prescription number is invalid, update helper cdt and end*/
      a!localVariables(
        local!invalidPrescriptionNumber: rule!AS_CO_UT_isBlank(
          value: rule!AS_GCM_BL_doesClausePrescriptionHtmlDocExist(
            clauses: ri!refClause,
            sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
          )
        ),
        local!prescriptionHtml: if(
          local!invalidPrescriptionNumber,
          "",
          rule!AS_GCM_INT_getAcquisitionDocumentHtml(
            uniqueNumber: ri!refClause[recordType!AS GCM R Clause.prescriptionNumber],
            sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
          )
        ),
        local!prescriptionText: if(
          index(local!prescriptionHtml, "success", null) = "true",
          rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
            responseBody: local!prescriptionHtml.result.body,
            isClauseText: false()
          ),
          ri!refClauseText.prescriptionText
        ),
        a!map(
          refClause: rule!AS_GCM_setRecordFields(
            record: ri!refClause,
            childRelationships: a!map(
              field: recordType!AS GCM R Clause.acquisitionUpdateHelper,
              value: recordType!AS GCM P Acquisition Update Helper(
                recordType!AS GCM P Acquisition Update Helper.refClauseId: ri!refClause[recordType!AS GCM R Clause.clauseId],
                recordType!AS GCM P Acquisition Update Helper.refClauseTextId: ri!refClauseText.clauseTextId,
                recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber: true,
                recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber: local!invalidPrescriptionNumber,
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextError: index(local!prescriptionHtml, "error", null),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess: index(local!prescriptionHtml, "success", null),
                recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned: if(
                  local!invalidPrescriptionNumber = true,
                  false,
                  rule!AS_CO_UT_isBlank(value: local!prescriptionText)
                ),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!prescriptionText),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClauseText.prescriptionText,
                      value2: local!prescriptionText
                    )
                  )
                )
              )
            )
          ),
          refClauseText: ri!refClauseText
        )
      ),
      /*--> if clause number is valid update helper cdt with the far matrix data*/
      a!localVariables(
        local!refSection: rule!AS_GCM_QR_getReferenceSectionData(
          executeWhen: true,
          contractFormatId: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_UCF
        ),
        /*Getting FAR Matrix data from integrations*/
        local!farMatrix: rule!AS_GCM_FAR_getFarMatrixData(
          clauseNumber: ri!refClause[recordType!AS GCM R Clause.clauseNumber],
          integrationResponseMaps: ri!integrationResponseMaps
        ),
        local!initialFarMatrixData: local!farMatrix.data,
        local!farMatrixData: rule!AS_CO_UT_rejectNullValuesFromArray(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!initialFarMatrixData,
            expression: if(
              tostring(
                trim(
                  fv!item[wherecontains(
                    rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "alternateValue"), fv!item.column
                  )].value
                )
              ) = touniformstring(
                ri!refClause[recordType!AS GCM R Clause.alternateValue]
              ),
              fv!item,
              null
            )
          )
        ),
        local!invalidClauseTitle: if(
          and(
            rule!AS_CO_UT_isNotBlank(local!initialFarMatrixData),
            rule!AS_CO_UT_isBlank(local!farMatrixData)
          ),
          true,
          false
        ),
        /*Updating the FAR matrix data to the ref clause (Upated as null when no value is returned*/
        local!farMatrixUpdatedClause: if(
          rule!AS_CO_UT_isBlank(local!farMatrixData),
          null,
          rule!AS_GCM_setRecordFields(
            record: ri!refClause,
            alwaysOverwrite: #"SYSTEM_SYSRULES_forEach"(
              items: local!farMatrixData,
              expression: a!map(
                field: rule!AS_GCM_BL_mapFarMatrixHeadersToClauseFields(column: fv!item.column),
                /*This will override the existing value in appian with the value from Far Matrix (Even when the FAR Matrix returns null*/
                value: rule!AS_GCM_BL_mapFarMatrixValuesToAssociatedClauseFieldValues(
                  header: fv!item.column,
                  value: fv!item.value,
                  refSection: local!refSection
                )
              )
            )
          )
        ),
        /*Fetch Clause text*/
        local!clauseHtml: rule!AS_GCM_INT_getAcquisitionDocumentHtml(
          uniqueNumber: rule!AS_CO_UT_replaceNull(
            nullableValue: local!farMatrixUpdatedClause[recordType!AS GCM R Clause.clauseNumber],
            replacementValue: ri!refClause[recordType!AS GCM R Clause.clauseNumber]
          ),
          sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
        ),
        local!clauseText: if(
          and(
            index(local!clauseHtml, "success", null) = "true",
            not(
              or(
                and(
                  rule!AS_CO_UT_isNotBlank(local!initialFarMatrixData),
                  rule!AS_CO_UT_isBlank(local!farMatrixData)
                ),
                local!invalidClauseNumber
              )
            )
          ),
          rule!AS_GCM_UT_formatMergedCells(
            rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
              responseBody: local!clauseHtml.result.body,
              isClauseText: true()
            )
          ),
          ri!refClauseText.clauseText
        ),
        /*Check if invalid Prescription number*/
        local!invalidPrescriptionNumber: rule!AS_CO_UT_isBlank(
          value: rule!AS_GCM_BL_doesClausePrescriptionHtmlDocExist(
            clauses: rule!AS_CO_UT_replaceNull(
              nullableValue: local!farMatrixUpdatedClause,
              replacementValue: ri!refClause
            ),
            sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
          )
        ),
        /*Fetch Prescription Text*/
        local!prescriptionHtml: if(
          local!invalidPrescriptionNumber = true,
          "",
          rule!AS_GCM_INT_getAcquisitionDocumentHtml(
            uniqueNumber: rule!AS_CO_UT_replaceNull(
              nullableValue: local!farMatrixUpdatedClause[recordType!AS GCM R Clause.prescriptionNumber],
              replacementValue: ri!refClause[recordType!AS GCM R Clause.prescriptionNumber]
            ),
            sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
          )
        ),
        local!prescriptionText: if(
          index(local!prescriptionHtml, "success", null) = "true",
          rule!AS_GCM_BL_retrieveTextFromHtmlDocument(
            responseBody: local!prescriptionHtml.result.body,
            isClauseText: false()
          ),
          ""
        ),
        local!effectiveDate: rule!AS_GCM_FAR_updateEffectiveDateFromSync(
          clauseHtml: local!clauseHtml,
          alternateValue: ri!refClause[recordType!AS GCM R Clause.alternateValue]
        ),
        local!fillIn: rule!AS_GCM_updateFillInFromSync(clauseHtml: local!clauseHtml),
        /*Update the Helper CDT*/
        a!map(
          refClause: rule!AS_GCM_setRecordFields(
            record: a!defaultValue(
              value: local!farMatrixUpdatedClause,
              default: ri!refClause
            ),
            alwaysOverwrite: {
              a!map(
                field: recordType!AS GCM R Clause.fillIn,
                value: if(
                  or(
                    local!invalidClauseNumber = true,
                    local!invalidClauseTitle = true,
                    rule!AS_CO_UT_isBlank(local!fillIn)
                  ),
                  ri!refClause[recordType!AS GCM R Clause.fillIn],
                  if(
                    toboolean(local!fillIn) = true,
                    true(),
                    null
                  )
                )
              ),
              a!map(
                field: recordType!AS GCM R Clause.effectiveDate,
                value: if(
                  or(
                    local!invalidClauseNumber = true,
                    local!invalidClauseTitle = true,
                    rule!AS_CO_UT_isBlank(local!effectiveDate)
                  ),
                  ri!refClause[recordType!AS GCM R Clause.effectiveDate],
                  local!effectiveDate
                )
              )
            },
            childRelationships: a!map(
              field: recordType!AS GCM R Clause.acquisitionUpdateHelper,
              value: recordType!AS GCM P Acquisition Update Helper(
                recordType!AS GCM P Acquisition Update Helper.refClauseId: ri!refClause[recordType!AS GCM R Clause.clauseId],
                recordType!AS GCM P Acquisition Update Helper.refClauseTextId: ri!refClauseText.clauseTextId,
                recordType!AS GCM P Acquisition Update Helper.farMatrixSuccess: index(local!farMatrix, "success", null),
                recordType!AS GCM P Acquisition Update Helper.farMatrixError: index(local!farMatrix, "error", null),
                recordType!AS GCM P Acquisition Update Helper.noDataReturned: if(
                  or(
                    local!invalidClauseNumber = true,
                    local!invalidClauseTitle = true
                  ),
                  false,
                  rule!AS_CO_UT_isBlank(local!initialFarMatrixData)
                ),
                recordType!AS GCM P Acquisition Update Helper.clauseDataUpdated: and(
                  rule!AS_CO_UT_isNotBlank(local!farMatrixUpdatedClause),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClause,
                      value2: local!farMatrixUpdatedClause
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.invalidClauseNumber: false,
                recordType!AS GCM P Acquisition Update Helper.invalidClauseTitle: local!invalidClauseTitle,
                recordType!AS GCM P Acquisition Update Helper.clauseTextError: index(local!clauseHtml, "error", null),
                recordType!AS GCM P Acquisition Update Helper.clauseTextSuccess: index(local!clauseHtml, "success", null) = "true",
                recordType!AS GCM P Acquisition Update Helper.noClauseTextReturned: if(
                  or(
                    local!invalidClauseNumber = true,
                    local!invalidClauseTitle = true
                  ),
                  false,
                  rule!AS_CO_UT_isBlank(value: local!clauseText)
                ),
                recordType!AS GCM P Acquisition Update Helper.clauseTextUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!clauseText),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClauseText.clauseText,
                      value2: local!clauseText
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.invalidPrescriptionNumber: local!invalidPrescriptionNumber,
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextError: index(local!prescriptionHtml, "error", null),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextSuccess: index(local!prescriptionHtml, "success", null),
                recordType!AS GCM P Acquisition Update Helper.noEffectiveDateReturned: if(
                  or(
                    local!invalidClauseNumber = true,
                    local!invalidClauseTitle = true
                  ),
                  false,
                  rule!AS_CO_UT_isBlank(value: local!effectiveDate)
                ),
                recordType!AS GCM P Acquisition Update Helper.effectiveDateUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!effectiveDate),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClause[recordType!AS GCM R Clause.effectiveDate],
                      value2: local!effectiveDate
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.noFillInReturned: if(
                  or(
                    local!invalidClauseNumber = true,
                    local!invalidClauseTitle = true
                  ),
                  false,
                  rule!AS_CO_UT_isBlank(value: local!fillIn)
                ),
                recordType!AS GCM P Acquisition Update Helper.fillInUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!fillIn),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClause[recordType!AS GCM R Clause.fillIn],
                      value2: local!fillIn
                    )
                  )
                ),
                recordType!AS GCM P Acquisition Update Helper.noPrescriptionTextReturned: if(
                  local!invalidPrescriptionNumber = true,
                  false,
                  rule!AS_CO_UT_isBlank(value: local!prescriptionText)
                ),
                recordType!AS GCM P Acquisition Update Helper.prescriptionTextUpdated: and(
                  rule!AS_CO_UT_isNotBlank(input: local!prescriptionText),
                  not(
                    rule!AS_CO_UT_compare(
                      value1: ri!refClauseText.prescriptionText,
                      value2: local!prescriptionText
                    )
                  )
                )
              )
            )
          ),
          refClauseText: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
            cdt: ri!refClauseText,
            fieldValuePairs: {
              {
                field: "clauseText",
                value: if(
                  and(
                    or(
                      local!invalidClauseNumber = true,
                      local!invalidClauseTitle = true
                    ),
                    rule!AS_CO_UT_isBlank(local!farMatrixData),
                    rule!AS_CO_UT_isBlank(local!clauseText)
                  ),
                  ri!refClauseText.clauseText,
                  local!clauseText
                )
              },
              {
                field: "prescriptionText",
                value: if(
                  and(
                    or(
                      local!invalidPrescriptionNumber = true,
                      local!invalidClauseNumber = true,
                      local!invalidClauseTitle = true
                    ),
                    rule!AS_CO_UT_isBlank(local!farMatrixData),
                    rule!AS_CO_UT_isBlank(local!prescriptionText)
                  ),
                  ri!refClauseText.prescriptionText,
                  local!prescriptionText
                )
              }
            }
          )
        )
      )
    )
  )
)

