_a-0000e6ec-ba89-8000-9be2-011c48011c48_.sail_code:
/*1. We can include all possible combination of months in the expression if we need to*/
/*2.Logic and patterns are different for original and alternate clauses*/
a!localVariables(
  /*Doc html from acquistion & removing html*/
  local!plainText: concat(split(striphtml(ri!clauseHtml), " ")),
  /*patterns to match with alternate clauses effective date in sam.gov*/
  local!pattern: "(Alternate|Alternate(.*?| ))(I|II|III|IV|V).*?(\((Jan|Feb|Mar|Apr|May|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec).*?\d{4}\)|\((Jan|Feb|Mar|Apr|May|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec).*?\d{2}\))(.Asprescribed|.*?Asprescribedin|. Asprescribedin|.Asprescribedin|Asprescribedin|.in|.if|.*?ifthedelivery|.*?Ifthecontracting|.When)",
  /*Extract end of clause*/
  local!extractEndClause: extract(
    local!plainText,
    "Endofclause",
    "Parenttopic"
  ),
  /*Extract end of Provision - End of Provision (caps)*/
  local!extractCapsEndProvision: extract(
    local!plainText,
    "EndofProvision",
    "Parenttopic"
  ),
  /*Extract end of provision - End of provision (small)*/
  local!extractSmallEndProvision: extract(
    local!plainText,
    "Endofprovision",
    "Parenttopic"
  ),
  /*using regex to get the  output*/
  local!regexMatch: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      regexallmatches(
        local!pattern,
        if(
          find("Endofclause", local!plainText) <> 0,
          local!extractEndClause,
          if(
            find("EndofProvision", local!plainText) <> 0,
            local!extractCapsEndProvision,
            if(
              find("Endofprovision", local!plainText) <> 0,
              local!extractSmallEndProvision,
              local!plainText
            )
          )
        )
      ),
      split(fv!item, "[Reserved]")
    )
  ),
  /*final pattern for month and year*/
  local!finalRegexSearch: "([(](Jan|Feb|Mar|Apr|May| Jun|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec)( |.*?)\d{4})|([(](Jan|Feb|Mar|Apr|May| Jun|Jun|June|Jul|July|Aug|Sep|Sept|Oct|Nov|Dec)( |.*?)\d{2})",
  /*fetch alternate I,II,III,IV & V effective dates*/
  local!fetchAlternateEffDates: #"SYSTEM_SYSRULES_forEach"(
    enumerate(5) + 1,
    if(
      or(
        ri!alternateValue = cons!AS_GCM_CLAUSE_TITLE_ALTERNATE_WITH_ROMAN_NUMBERS[fv!item],
        ri!alternateValue = cons!AS_GCM_CLAUSE_TITLE_ROMAN_NUMBERS_FOR_ALTERNATE[fv!item]
      ),
      if(
        count(local!regexMatch) < fv!index,
        null,
        index(
          rule!AS_GCM_UT_regexsearch(
            pattern: local!finalRegexSearch,
            searchString: local!regexMatch[fv!index],
            regexFlag: "i"
          ),
          "match",
          null
        )
      ),
      null
    )
  ),
  /*removing special characters*/
  local!removeSpclChars: concat(
    split(
      local!fetchAlternateEffDates,
      { cons!AS_GCM_SPECIAL_CHARACTERS, " " }
    )
  ),
  /*As of now- there is one clause(52.214-28) where year format is just 2 digit. So to match with 4 digit- we are adding '20' as prefix*/
  local!result: if(
    len(local!removeSpclChars) = 5,
    insert(local!removeSpclChars, "20", 4),
    local!removeSpclChars
  ),
  if(
    rule!AS_CO_UT_isBlank(local!fetchAlternateEffDates),
    null,
    left(local!result, 3) & " " & right(local!result, 4)
  )
)

