/*This rule with replace all texts in ri!find with "" if null is passed to ri!replaceWith
  If null is passed to ri!instanceNum then only the first instance of ri!find will be replaced 
*/
if(
  rule!AS_CO_UT_isBlank(ri!find),
  ri!text,
  reduce(
    fn!substitute,
    ri!text,
    if(
      ri!replaceAllInstances = true(),
      merge(
        ri!find,
        a!defaultValue(
          value: ri!replaceWith,
          default: repeat(length(ri!find), "")
        )
      ),
      merge(
        ri!find,
        a!defaultValue(
          value: ri!replaceWith,
          default: repeat(length(ri!find), "")
        ),
        a!defaultValue(
          value: ri!instanceNum,
          default: repeat(length(ri!find), 1)
        )
      )
    )
  )
)
