    rule!AS_CO_UT_isBlank,
    { ri!refClauses, ri!refClauseTexts }
  ),
  rule!AS_CO_UT_initializeBlankLocalVariable(
    type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_R_Clause_Helper'
  ),
  /*Combining AS_GCM_R_Clause and AS_GCM_R_Clause_Texts*/
  a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!refClauses,
      expression: rule!AS_GCM_UT_nullifyEmptyNestedCdtFields(
        cdt: rule!AS_CO_UT_cast(
          type: 'type!{urn:com:appian:types:AS:GCM}AS_GCM_R_Clause_Helper',
          value: {
            clauseId: fv!item[recordType!AS GCM R Clause.clauseId],
            clauseNumber: fv!item[recordType!AS GCM R Clause.clauseNumber],
            clauseTitle: fv!item[recordType!AS GCM R Clause.clauseTitle],
            alternateValue: fv!item[recordType!AS GCM R Clause.alternateValue],
            effectiveDate: fv!item[recordType!AS GCM R Clause.effectiveDate],
            ucfSection: recordType!AS GCM R Section(
              recordType!AS GCM R Section.sectionId: fv!item[recordType!AS GCM R Clause.ucfSectionId]
            ),
            refSource: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.refSourceId]
            ),
            refType: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.refTypeId]
            ),
            refFormat: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.refFormatId]
            ),
            fillIn: fv!item[recordType!AS GCM R Clause.fillIn],
            editable: fv!item[recordType!AS GCM R Clause.editable],
            prescriptionNumber: fv!item[recordType!AS GCM R Clause.prescriptionNumber],
            fpSup: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.fpSupId]
            ),
            crSup: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.crSupId]
            ),
            fpRd: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.fpRdId]
            ),
            crRd: recordType!AS GCM R Data(
              recordType!AS GCM R Data.refDataId: fv!item[recordType!AS GCM R Clause.crRdId]
