_a-0000e615-fd0f-8000-9bc1-011c48011c48_.sail_code:
rule!AS_CO_UT_queryRecord(
  recordType: recordType!AS GCM R Clause,
  returnType: a!defaultValue(
    value: ri!returnType,
    default: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY
  ),
  executeWhen: and(
    ri!executeWhen,
    or(
      any(
        rule!AS_CO_UT_isNotBlank(_),
        {
          ri!additionalFilters,
          ri!logicalExpression,
          ri!aggregationFields
        }
      ),
      rule!AS_CO_UT_isNotBlank(ri!clauseIds),
      rule!AS_CO_UT_isNotBlank(ri!clauseNumbers),
      ri!returnType = cons!AS_CO_ENUM_QE_RETURN_TYPE_TOTAL_COUNT
    )
  ),
  fields: rule!AS_CO_UT_rejectNullValuesFromArray(
    {
      ri!fields,
      if(
        ri!includeRelatedRecords,
        {
          recordType!AS GCM R Clause.refFormat,
          recordType!AS GCM R Clause.ucfSection,
          recordType!AS GCM R Clause.refSource,
          recordType!AS GCM R Clause.refType,
          recordType!AS GCM R Clause.additionalSections
        },
        {}
      )
    }
  ),
  /* To ensure proper null checks, we'll force all queryFilter values to not be null */
  logicalExpression: ri!logicalExpression,
  pagingInfo: ri!pagingInfo,
  triggerRefresh: ri!triggerRefresh,
  filters: {
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.clauseId,
      operator: "in",
      value: ri!clauseIds
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.clauseNumber,
      operator: "in",
      value: ri!clauseNumbers
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.clauseNumberWithAlternate,
      operator: "in",
      value: #"SYSTEM_SYSRULES_forEach"(
        items: ri!alternateValues,
        expression: joinarray({ ri!clauseNumbers[1], fv!item }, " | ")
      ),
      applyWhen: and(
        /*  For Efficiency we enfore the ri!clauseNumbers value to be exactly 1     */
        rule!AS_GAM_UT_ArrayLength(ri!clauseNumbers) = 1,
        rule!AS_CO_UT_isNotBlank(ri!alternateValues)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Clause.deviation,
      operator: "=",
      value: false,
      applyWhen: rule!AS_CO_UT_booleanDefaultFalse(ri!excludeDeviations)
    ),
    ri!additionalFilters
  },
  aggregationFields: ri!aggregationFields,
  relatedRecordData: ri!relatedRecordData
)

