_a-0000e3e6-fb42-8000-9ba5-011c48011c48_.sail_code:
a!localVariables(
  /*Get the distinct IDs*/
  local!distinctIds: rule!AS_CO_UT_distinct(
    tointeger(
      {
        index(
          ri!originalObjects,
          ri!auditConfig.idField,
          {}
        ),
        index(
          ri!newObjects,
          ri!auditConfig.idField,
          {}
        )
      }
    )
  ),
  if(
    rule!AS_CO_UT_isBlank(local!distinctIds),
    {},
    /*This flatten converts to a list of dictionary, which prevents odd behavior in the parent*/
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!distinctIds,
        expression: a!flatten(
          rule!AS_ADT_UT_calculateAudit_SingleObject(
            originalObject: index(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!originalObjects,
                field: ri!auditConfig.idField,
                value: fv!item
              ),
              1,
              {}
            ),
            newObject: index(
              rule!AS_CO_UT_filterCdtByField(
                cdt: ri!newObjects,
                field: ri!auditConfig.idField,
                value: fv!item
              ),
              1,
              {}
            ),
            auditConfig: ri!auditConfig,
            username: ri!username,
            externalAction: ri!externalAction,
            auditType: ri!auditType
          )
        )
      )
    )
  )
)

_a-0000eb32-2fbb-8000-9d0d-011c48011c48_.sail_code:
if(
  a!isNullOrEmpty(ri!record),
  ri!record,
  a!localVariables(
    local!allUpdates: rule!AS_CO_UT_rejectNullValuesFromArray(
      a!flatten(
        {
          ri!alwaysOverwrite,
          ri!childRelationships,
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!neverOverwrite,
            expression: #"urn:appian:function:v1:a:update"(
              fv!item,
              "value",
              /*Specifically using this instead of a!defaultValue() to account for the `{null}` value*/
              rule!AS_CO_UT_replaceNull(
                nullableValue: ri!record[fv!item.field],
                replacementValue: fv!item.value
              )
            )
          )
        }
      )
    ),
    local!onlyChangedUpdates: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!allUpdates,
        expression: if(
          or(
            tostring(ri!record[fv!item.field]) = tostring(fv!item.value),
            toboolean(fv!item.applyWhen) = false
          ),
          {},
          fv!item
        )
      )
    ),
    if(
      a!isNullOrEmpty(local!onlyChangedUpdates),
      ri!record,
      #"urn:appian:function:v1:a:update"(
        data: ri!record,
        index: local!onlyChangedUpdates.field,
        value: local!onlyChangedUpdates.value
      )
    )
  )
)

