_a-0000ebc5-d78d-8000-9d55-011c48011c48_.sail_code:
rule!AS_CO_UT_cast(
  type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
    recordType!AS GCM R Clause
  ),
  value: #"SYSTEM_SYSRULES_forEach"(
    items: ri!clauseImportData.data,
    expression: a!localVariables(
      local!clauseImportData: fv!item,
      local!clauseFieldValues: rule!AS_CO_UT_dynamicDictionary(
        fieldValuePairs: #"SYSTEM_SYSRULES_forEach"(
          items: ri!clauseFields.field,
          expression: {
            field: fv!item,
            value: local!clauseImportData[displayvalue(
              fv!item, touniformstring(ri!excelColumns.clauseField.field), ri!excelColumns.excelHeader, null
            )]
          }
        )
      ),
      recordType!AS GCM R Clause(
        recordType!AS GCM R Clause.clauseNumber: local!clauseFieldValues.clauseNumber,
        recordType!AS GCM R Clause.alternateValue: local!clauseFieldValues.alternateValue,
        recordType!AS GCM R Clause.deviation: a!defaultValue(
          value: local!clauseFieldValues.deviation,
          default: false
        ),
        recordType!AS GCM R Clause.clauseNumberWithAlternate: joinarray(
          {
            local!clauseFieldValues.clauseNumber,
            local!clauseFieldValues.alternateValue,

          },
          " | "
        ),
        recordType!AS GCM R Clause.ucfSectionId: displayvalue(
          local!clauseFieldValues.ucfSection.sectionLabel,
          ri!refSection[recordType!AS GCM R Section.sectionLabel],
          ri!refSection[recordType!AS GCM R Section.sectionId],
          null
        ),
        recordType!AS GCM R Clause.refTypeId: displayvalue(
          local!clauseFieldValues.refType.refLabel,
          ri!refType[recordType!AS GCM R Data.refLabel],
          ri!refType[recordType!AS GCM R Data.refDataId],
          null
        ),
        recordType!AS GCM R Clause.refFormatId: if(
          a!isNullOrEmpty(
            local!clauseFieldValues.refFormat.refLabel
          ),
          null,
          a!defaultValue(
            value: displayvalue(
              local!clauseFieldValues.refFormat.refLabel,
              ri!refFormat[recordType!AS GCM R Data.refDescription],
              ri!refFormat[recordType!AS GCM R Data.refDataId],
              null
            ),
            default: displayvalue(
              local!clauseFieldValues.refFormat.refLabel,
              ri!refFormat[recordType!AS GCM R Data.refLabel],
              ri!refFormat[recordType!AS GCM R Data.refDataId],
              null
            )
          )
        ),
        recordType!AS GCM R Clause.additionalSections: {
          recordType!AS GCM R Clause Section Mapping(
            recordType!AS GCM R Clause Section Mapping.sectionId: rule!AS_GCM_filterRecords(
              records: ri!refSection,
              fieldValuePairs: {
                a!map(
                  field: recordType!AS GCM R Section.contractFormatId,
                  value: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_COMMERCIAL
                ),
                a!map(
                  field: recordType!AS GCM R Section.sectionLabel,
                  value: local!clauseFieldValues.commercialSection.sectionLabel
                )
              }
            )[recordType!AS GCM R Section.sectionId],
            recordType!AS GCM R Clause Section Mapping.refSection: recordType!AS GCM R Section(
              recordType!AS GCM R Section.contractFormatId: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_COMMERCIAL
            )
          ),
          recordType!AS GCM R Clause Section Mapping(
            recordType!AS GCM R Clause Section Mapping.sectionId: rule!AS_GCM_filterRecords(
              records: ri!refSection,
              fieldValuePairs: {
                a!map(
                  field: recordType!AS GCM R Section.contractFormatId,
                  value: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_CONSTRUCTION
                ),
                a!map(
                  field: recordType!AS GCM R Section.sectionLabel,
                  value: local!clauseFieldValues.constructionSection.sectionLabel
                )
              }
            )[recordType!AS GCM R Section.sectionId],
            recordType!AS GCM R Clause Section Mapping.refSection: recordType!AS GCM R Section(
              recordType!AS GCM R Section.contractFormatId: cons!AS_GCM_REF_ID_CONTRACT_FORMAT_CONSTRUCTION
            )
          )
        }
      )
    )
  )
)

_a-0000e5da-a251-8000-9bbe-011c48011c48_.sail_code:
a!localVariables(
  /*Indexing 1 to convert list of dictionary to list*/
  local!complexFieldConfig: index(
    rule!AS_CO_UT_filterCdtByField(
      cdt: ri!auditParams.auditConfig.complexFields,
      field: "auditFieldName",
      value: tostring(ri!auditParams.complexFieldName)
    ),
    1,
    null
  ),
  local!foreignIdField: tostring(
    a!flatten(
      index(
        local!complexFieldConfig,
        "foreignIdField",
        null
      )
    )
  ),
  local!auditConfig: rule!AS_CO_UT_dynamicDictionary(
    fieldValuePairs: {
      /*Field value pairs for parent object audit*/
      { field: "username", value: ri!initiator },
      { field: "timestamp", value: now() },
      {
        field: "auditActionCode",
        value: ri!auditParams.externalAction
      },
      /*Indexing the "idField" of the parent object using "foreignIdField"*/
      if(
        rule!AS_CO_UT_isBlank(local!foreignIdField),
        {},
        {
          field: ri!auditParams.auditConfig.idField,
          value: tointeger(
            rule!AS_CO_UT_replaceNull(
              nullableValue: index(
                rule!AS_CO_UT_indexPath(
                  cdt: ri!auditParams.newObjects,
                  field: ri!auditParams.auditConfig.idField
                ),
                1,
                null
              ),
              replacementValue: index(
                rule!AS_CO_UT_indexPath(
                  cdt: ri!auditParams.newObjects,
                  field: local!foreignIdField
                ),
                1,
                null
              )
            )
          )
        }
      ),
      {
        field: tostring(ri!auditParams.complexFieldName),
        value: if(
          rule!AS_CO_UT_isBlank(ri!auditParams.manipulationAudit),
          /*If the audit calculation for the nested CDT involves generic field changes (i.e the manipulation audit is null), we can directly use the field change rule to calculate the audit*/
          rule!AS_ADT_UT_calculateAudit(
            originalObjects: rule!AS_CO_UT_castToDictionaryList(value: ri!auditParams.originalObjects),
            newObjects: rule!AS_CO_UT_castToDictionaryList(value: ri!auditParams.newObjects),
            objectTypeNumber: ri!auditParams.objectTypeNumber,
            auditConfig: rule!AS_CO_UT_castToDictionary(value: local!complexFieldConfig),
            namespace: ri!auditParams.namespace,
            username: ri!initiator,
            externalAction: null
          ),
          /*If the audit data is nested and is manipulated and passed from outsite, we can directly construct the audit entry for each ri!auditParams.manipulationAudit*/
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!auditParams.manipulationAudit,
            expression: rule!AS_CO_UT_dynamicDictionary(
              fieldValuePairs: {
                if(
                  rule!AS_CO_UT_isNotBlank(local!complexFieldConfig.idField),
                  {
                    field: local!complexFieldConfig.idField,
                    value: index(
                      ri!auditParams.newObjects,
                      tostring(local!complexFieldConfig.idField),
                      null
                    )
                  },
                  {}
                ),
                { field: "username", value: ri!initiator },
                { field: "timestamp", value: now() },
                {
                  field: "auditActionCode",
                  value: /*If the action code varies for each manipulation audit, actionCode within auditParams.manipulationAudit will be used. Else, externalAction will be used*/
                  rule!AS_CO_UT_replaceNull(
                    nullableValue: fv!item.actionCode,
                    replacementValue: ri!auditParams.externalAction
                  )
                },
                if(
                  rule!AS_CO_UT_isNotBlank(
                    local!complexFieldConfig.foreignIdField
                  ),
                  {
                    field: local!complexFieldConfig.foreignIdField,
                    value: index(
                      rule!AS_CO_UT_indexPath(
                        cdt: ri!auditParams.newObjects,
                        field: local!complexFieldConfig.foreignIdField
                      ),
                      1,
                      null
                    )
                  },
                  {}
                ),
                /*The field changes will be passed as label value pairs in "fieldChanges" column of auditParams.manipulationAudit"*/
                {
                  field: "simpleFieldChanges",
                  value: rule!AS_CO_UT_castToDictionaryList(value: fv!item.fieldChanges)
                }
              }
            )
          )
        )
      }
    }
  ),
  if(
    /*To make the action code "UNCHANGED" if the object is unmodified(excluding the metadata)*/
    rule!AS_CO_UT_not(
      contains(
        a!flatten(
          {
            rule!AS_CO_UT_isBlank(
              index(
                rule!AS_CO_UT_indexPath(
                  cdt: local!auditConfig,
                  field: tostring(ri!auditParams.complexFieldName)
                ),
                "simpleFieldChanges",
                null
              )
            ),
            #"SYSTEM_SYSRULES_forEach"(
              items: {
                index(
                  index(
                    local!complexFieldConfig,
                    "complexFields",
                    null
                  ),
                  "auditFieldName",
                  null
                )
