/*If the audit data is not nested and is manipulated and passed from outsite, we can directly construct the audit entry for each ri!auditParams.manipulationAudit*/
#"SYSTEM_SYSRULES_forEach"(
  items: ri!auditParams.manipulationAudit,
  expression: rule!AS_CO_UT_dynamicDictionary(
    fieldValuePairs: {
      if(
        rule!AS_CO_UT_isBlank(
          index(
            ri!auditParams.auditConfig,
            "idField",
            null
          )
        ),
        {},
        {
          field: ri!auditParams.auditConfig.idField,
          value: index(
            index(
              ri!auditParams.newObjects,
              tostring(ri!auditParams.auditConfig.idField),
              null
            ),
            1,
            null
          )
        }
      ),
      { field: "username", value: ri!initiator },
      { field: "timestamp", value: now() },
      {
        field: "auditActionCode",
        value: /*If the action code varies for each manipulation audit, actionCode within auditParams.manipulationAudit will be used. Else, externalAction will be used*/
        rule!AS_CO_UT_replaceNull(
          nullableValue: fv!item.actionCode,
          replacementValue: ri!auditParams.externalAction
        )
      },
      if(
        rule!AS_CO_UT_isNotBlank(
          ri!auditParams.auditConfig.foreignIdField
        ),
        {
          field: ri!auditParams.auditConfig.foreignIdField,
          value: index(
            index(
              ri!auditParams.newObjects,
              tostring(
                ri!auditParams.auditConfig.foreignIdField
              ),
              null
            ),
            1,
            null
          )
        },
        {}
      ),
      /*The field changes will be passed as label value pairs in "fieldChanges" column of auditParams.manipulationAudit"*/
      {
        field: "simpleFieldChanges",
        value: rule!AS_CO_UT_castToDictionaryList(value: fv!item.fieldChanges)
      }
    }
  )
)

