  local!hasHeaders: true,
  local!excelData: readexcelimportfile(
    excelDocument: ri!excelDocument,
    sheetNumber: rule!AS_CO_UT_replaceNull(
      nullableValue: local!sheetNumber,
      replacementValue: 0
    ),  
    startRow: 0,
    numberOfColumns: ri!numberOfColumns
  ),
  local!importedData:if(
    rule!AS_CO_UT_not(
      local!excelData.success
    ),
    rule!AS_CO_UT_returnNullDictionary(),
    a!localVariables(
      local!headers: #"SYSTEM_SYSRULES_forEach"(
        items: index(
          local!excelData.result,
          1,
          "values",
          {}
        ),
        expression: if(
          local!hasHeaders,
          fv!item,
          concat(
            "col",
            fv!index
          )
        )
      ),
      {
        sheetNumber: local!sheetNumber,
        headers: touniformstring(
          local!headers
        ),
        data: if(
          rule!AS_CO_UT_isBlank(
            local!excelData.result
          ),
          rule!AS_CO_UT_returnNullDictionaryList(),
          a!flatten(#"SYSTEM_SYSRULES_forEach"(
            items: local!excelData.result.values,
            expression: if(
              and(
                local!hasHeaders,
                fv!isFirst
              ),
              {},
              rule!AS_CO_UT_updateCdtFields(
                cdt: rule!AS_CO_UT_returnNullDictionary(),
                fields: local!headers,
                newValues: fv!item
              )
            )
          ))
        )
      }
    )
  ),
  if(rule!AS_CO_UT_isBlank(local!importedData.data),null,local!importedData)
)

